(3, 'kem chống nắng ', 'KCN1', '230000', 10, '1654180951_kemcn.jpg', 'ĐFFG', 'QWER', 1, 4, 2),
(4, 'Son BlackRouge', 'SONBR', '250000', 13, '1654239269_11042022_035233_PM_SonBlackRouge.png', 'qqqq', 'ddd', 1, 2, 1),
(5, 'Mặt Nạ Bí Đao Cocoon ', 'MN01', '295000', 20, '1655712642_MN001.png', '<p>Mặt Nạ B&iacute; Đao Cocoon Winter Melon Face Mask With Centella &amp; Tea Tree Giảm Dầu v&agrave; Mụn 100ml gồm th&agrave;nh phần ch&iacute;nh l&agrave; b&iacute; đao thanh m&aacute;t, bổ sung th&ecirc;m rau m&aacute; v&agrave; tinh dầu tr&agrave;m tr&agrave; gi&uacute;p giảm nhờn, l&agrave;m th&ocirc;ng tho&aacute;ng lỗ ch&acirc;n l&ocirc;ng</p>\r\n', '<p><strong>Th&agrave;nh phần ch&iacute;nh v&agrave; c&ocirc;ng dụng:</strong><br />\r\n- B&iacute; đao: Theo s&aacute;ch y học cổ truyền, b&iacute; đao c&oacute; đặc t&iacute;nh l&agrave;m m&aacute;t, l&agrave;m giảm nhiệt, kh&aacute;ng vi&ecirc;m v&agrave; diệt khuẩn gi&uacute;p điều trị mụn trứng c&aacute;, mụn vi&ecirc;m.<br />\r\n- Rau m&aacute;: C&aacute;c hợp chất sinh học như axit asiatic, asiaticoside, axit madecassic v&agrave; madecassoside c&oacute; trong rau m&aacute; l&agrave; những chất ch&iacute;nh gi&uacute;p tăng sinh collagen cho l&agrave;n da, kh&aacute;ng vi&ecirc;m, l&agrave;m dịu c&aacute;c vết đỏ v&agrave; chống k&iacute;ch ứng.<br />\r\n- Tinh dầu tr&agrave;m tr&agrave;: Loại tinh dầu c&oacute; m&ugrave;i thơm ấm &aacute;p, cay nồng gi&uacute;p trị mụn trứng c&aacute;, vết thương, c&ocirc;n tr&ugrave;ng cắn v&agrave; nhiễm tr&ugrave;ng nấm men.</p>\r\n\r\n<p><strong>Đối tượng sử dụng:</strong><br />\r\nMặt Nạ B&iacute; Đao Cocoon Winter Melon Face Mask With Centella &amp; Tea Tree Giảm Dầu v&agrave; Mụn 100ml ph&ugrave; hợp sử dụng cho mọi loại da.<br />\r\n<br />\r\n<strong>Hướng dẫn sử dụng:</strong><br />\r\n- Thoa đều một lượng vừa đủ tr&ecirc;n da sạch, tr&aacute;nh v&ugrave;ng mắt, để 20 ph&uacute;t rồi rửa sạch với nước. D&ugrave;ng 4-5 lần một tuần để đạt hiệu quả tốt nhất.</p>\r\n\r\n<p>Nơi sản xuất</p>\r\n\r\n<p>Việt Nam</p>\r\n\r\n<p>Th&agrave;nh phần</p>\r\n\r\n<p>Water, Illite, Montmorillonite, Kaolin, Butylene Glycol, Niacinamide, Propanediol, Glycerin, Benincasa Cerifera Fruit Extract, Centella Asiatica Extract, Epilobium Angustifolium Flower/Leaf/Stem Extract, Phellodendron Amurense Bark Extract, Betaine..</p>\r\n\r\n<p>Điều kiện bảo quản</p>\r\n\r\n<p>Nơi kh&ocirc; r&aacute;o, tr&aacute;nh &aacute;nh nắng</p>\r\n', 1, 9, 11),
(7, 'Mặt Nạ Đất Sét Some By Mi ', 'MN03', '200000', 15, '1655714012_MN03.png', '<p><strong>Mặt Nạ Đất S&eacute;t Some By Mi Super Matcha Pore Clean Clay Mask</strong>&nbsp;l&agrave; sản phẩm&nbsp;mới ra mắt thuộc d&ograve;ng Super Matcha, được <strong>SOME BY ME</strong>&nbsp;thiết kế chuy&ecirc;n biệt cho t&igrave;nh trạng da dầu, th&ocirc; r&aacute;p &amp; lỗ ch&acirc;n l&ocirc;ng to.&nbsp;</p>\r\n', '<p>&nbsp;C&ocirc;ng thức chứa th&agrave;nh phần Đất S&eacute;t gi&uacute;p hấp thụ dầu thừa &amp; l&agrave;m sạch s&acirc;u lỗ ch&acirc;n l&ocirc;ng, c&ugrave;ng chiết xuất Tr&agrave; Xanh Matcha gi&uacute;p giảm thiểu c&aacute;c dấu hiệu l&atilde;o h&oacute;a &amp; mang lại l&agrave;n da tươi s&aacute;ng. Đặc biệt, sản phẩm c&ograve;n gi&uacute;p cung cấp độ ẩm, kh&ocirc;ng l&agrave;m da kh&ocirc; căng sau khi đắp.</p>\r\n\r\n<p><strong>Mặt Nạ Đất S&eacute;t Some By Mi L&agrave;m Sạch Lỗ Ch&acirc;n L&ocirc;ng Super Matcha Pore Clean Clay Mask&nbsp;100g&nbsp;</strong>hiện đ&atilde; c&oacute; mặt tại&nbsp;<strong>Hasaki</strong>.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Mặt Nạ Đất Sét Some By Mi Super Matcha Pore Clean Clay Mask mang lại làn da sạch thoáng, săn chắc mịn màng\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/mat-na-dat-set-some-by-mi-lam-sach-lo-chan-long-100g-3.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Loại da ph&ugrave; hợp:</strong></h2>\r\n\r\n<ul>\r\n	<li>Sản phẩm ph&ugrave; hợp cho mọi loại da, đặc biệt l&agrave; da dầu v&agrave; da mụn.</li>\r\n</ul>\r\n\r\n<h2><strong>Giải ph&aacute;p cho t&igrave;nh trạng da:</strong></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Bề mặt da th&ocirc; r&aacute;p mặc d&ugrave; da tiết nhiều dầu</p>\r\n	</li>\r\n	<li>\r\n	<p>Nếu da thường xuy&ecirc;n gặp vấn đề do b&iacute; tắc lỗ ch&acirc;n l&ocirc;ng như mụn đầu đen, mụn sưng vi&ecirc;m&hellip;</p>\r\n	</li>\r\n	<li>\r\n	<p>Bề mặt da d&agrave;y, th&ocirc; r&aacute;p do tế b&agrave;o chết nhiều tr&ecirc;n da</p>\r\n	</li>\r\n	<li>\r\n	<p>Cho người muốn chăm s&oacute;c lỗ ch&acirc;n l&ocirc;ng thường xuy&ecirc;n</p>\r\n	</li>\r\n	<li>\r\n	<p>Cho người đang lo lắng về lỗ ch&acirc;n l&ocirc;ng như lỗ ch&acirc;n ch&acirc;n l&ocirc;ng bị gi&atilde;n nở, chảy xệ&hellip;</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2><strong>Ưu thế nổi bật:</strong></h2>\r\n\r\n<p><strong>Mặt Nạ Đất S&eacute;t Some By Mi Super Matcha Pore Clean Clay Mask&nbsp;</strong>gi&uacute;p l&agrave;m sạch s&acirc;u lỗ ch&acirc;n l&ocirc;ng, mang lại cho bạn l&agrave;n da sạch tho&aacute;ng:</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Th&agrave;nh phần từ&nbsp;<strong>Đất S&eacute;t (Clay)&nbsp;</strong>l&agrave;m sạch s&acirc;u lỗ ch&acirc;n l&ocirc;ng bằng c&aacute;ch hấp thụ chất thải, b&atilde; nhờn s&acirc;u trong lỗ ch&acirc;n l&ocirc;ng ra khỏi da, loại bỏ tế b&agrave;o chết, thanh lọc l&agrave;n da xỉn m&agrave;u.</p>\r\n	</li>\r\n	<li>\r\n	<p>Th&agrave;nh phần&nbsp;<strong>Tr&agrave; Xanh (Matcha)</strong>&nbsp;gi&agrave;u chất chống oxy h&oacute;a cung cấp dưỡng chất cho l&agrave;n da mệt mỏi, giảm thiểu dấu hiệu l&atilde;o h&oacute;a, gi&uacute;p da tươi s&aacute;ng hơn.</p>\r\n	</li>\r\n	<li>\r\n	<p>L&agrave;m sạch da chỉ trong 15 ph&uacute;t, gi&uacute;p bề mặt da th&ocirc; r&aacute;p trở n&ecirc;n mịn m&agrave;ng hơn.</p>\r\n	</li>\r\n	<li>\r\n	<p>Dưỡng ẩm cho da sau khi l&agrave;m sạch: chứa c&aacute;c loại tinh dầu cung cấp độ ẩm cho da, l&agrave;m dịu da, kh&ocirc;ng l&agrave;m da bị kh&ocirc; sau khi sử dụng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><img alt=\"Mặt Nạ Đất Sét Some By Mi Super Matcha Pore Clean Clay Mask 100g\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/mat-na-dat-set-some-by-mi-lam-sach-lo-chan-long-100g-1.jpg\" style=\"width:800px\" /></p>\r\n', 1, 9, 7),
(21, ' Nước Tẩy Trang Cocoon Bí Đao ', 'TT01', '180000', 20, '1655720965_TT2.png', '<p><strong><strong>Nước Tẩy Trang B&iacute; Đao Cocoon Winter Melon Micellar Water&nbsp;</strong></strong>mới từ thương hiệu Cocoon&nbsp;l&agrave; sản phẩm tẩy trang&nbsp;được thiết kế chuy&ecirc;n biệt d&agrave;nh cho da dầu v&agrave; da mụn nhạy cảm.&nbsp;Với c&ocirc;ng nghệ Micellar, nước tẩy trang b&iacute; đao gi&uacute;p l&agrave;m sạch hiệu quả lớp trang điểm, bụi bẩn v&agrave; dầu thừa, mang lại l&agrave;n da sạch ho&agrave;n to&agrave;n v&agrave; dịu nhẹ.</p>\r\n', '<p>L&agrave;n da dầu mụn thường nhạy cảm v&agrave; cần được sự chăm s&oacute;c nhiều hơn b&igrave;nh thường, đặc biệt l&agrave; d&acirc;n th&agrave;nh thị sinh sống trong m&ocirc;i trường nhiều kh&oacute;i bụi v&agrave; &ocirc; nhiễm nặng như hiện nay. C&aacute;c tạp chất c&ugrave;ng b&atilde; nhờn dư thừa t&iacute;ch tụ l&acirc;u dần sẽ khiến lỗ ch&acirc;n l&ocirc;ng b&iacute;t tắc - nguy&ecirc;n nh&acirc;n ch&iacute;nh dẫn đến h&igrave;nh th&agrave;nh mụn.</p>\r\n\r\n<p>Do đ&oacute;, việc l&agrave;m sạch lu&ocirc;n l&agrave; bước quan trọng nhất trong quy tr&igrave;nh chăm s&oacute;c da hằng ng&agrave;y. V&igrave; thế, Cocoon đ&atilde; nghi&ecirc;n cứu v&agrave; cho ra đời sản phẩm&nbsp;<strong>Nước Tẩy Trang B&iacute; Đao Cocoon Winter Melon Micellar Water</strong>&nbsp;đ&aacute;p ứng những ti&ecirc;u ch&iacute; khắc khe trong việc chăm s&oacute;c da dầu mụn: SẠCH S&Acirc;U, DỊU NHẸ, THO&Aacute;NG DA KH&Ocirc;NG G&Acirc;Y MỤN ẨN, KIỂM SO&Aacute;T DẦU.</p>\r\n\r\n<p><strong>Nước Tẩy Trang B&iacute; Đao Cocoon Winter Melon Micellar Water&nbsp;</strong>hiện đ&atilde; c&oacute; mặt tại&nbsp;<strong>Hasaki&nbsp;</strong>với 2 dung t&iacute;ch:&nbsp;</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>140ml</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>500ml</strong></p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><img alt=\"Nước Tẩy Trang Bí Đao Cocoon Winter Melon Micellar Water hiện đã có mặt tại Hasaki\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/nuoc-tay-trang-bi-dao-cocoon-lam-sach-giam-dau-140ml-3.jpg\" style=\"width:800px\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Loại da ph&ugrave; hợp:</h3>\r\n\r\n<ul>\r\n	<li>Sản phẩm ph&ugrave; hợp cho da dầu, da mụn, da nhạy cảm.</li>\r\n</ul>\r\n\r\n<h3>Giải ph&aacute;p cho t&igrave;nh trạng da:</h3>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Dầu thừa - lỗ ch&acirc;n l&ocirc;ng to</p>\r\n	</li>\r\n	<li>\r\n	<p>Da mụn hoặc dễ nổi mụn</p>\r\n	</li>\r\n	<li>\r\n	<p>Da nhạy cảm, dễ k&iacute;ch ứng</p>\r\n	</li>\r\n</ul>\r\n\r\n<h3>C&ocirc;ng dụng ch&iacute;nh:</h3>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>C&ocirc;ng nghệ Micellar</strong>&nbsp;gi&uacute;p dễ d&agrave;ng&nbsp;l&agrave;m sạch s&acirc;u lớp trang điểm, bụi bẩn v&agrave; b&atilde; nhờn tr&ecirc;n bề mặt da v&agrave; s&acirc;u trong lỗ ch&acirc;n l&ocirc;ng.</p>\r\n	</li>\r\n	<li>\r\n	<p>Chứa<strong>&nbsp;c&aacute;c chiết xuất thi&ecirc;n nhi&ecirc;n từ B&iacute; Đao, Rau M&aacute;, Tr&agrave;m Tr&agrave;</strong>&nbsp;gi&uacute;p kiểm so&aacute;t dầu thừa, kh&aacute;ng khuẩn - kh&aacute;ng vi&ecirc;m, l&agrave;m dịu m&aacute;t da, hỗ trợ&nbsp;trong việc chăm s&oacute;c da mụn.</p>\r\n	</li>\r\n	<li>\r\n	<p>Dưỡng ẩm da v&agrave; bảo vệ da khỏi&nbsp;căng thẳng từ m&ocirc;i trường b&ecirc;n ngo&agrave;i như bức xạ UV, &ocirc; nhiễm m&ocirc;i trường với<strong>&nbsp;Betaine</strong>&nbsp;<strong>chiết xuất từ củ cải đường</strong>.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&aacute;ng khuẩn v&agrave; loại bỏ vi khuẩn g&acirc;y mụn với&nbsp;<strong>O-Cymen-5-Ol v&agrave; Cetylpyridinium chloride</strong>.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kết cấu dạng lỏng tươi m&aacute;t, dịu nhẹ, th&ocirc;ng tho&aacute;ng v&agrave; kh&ocirc;ng l&agrave;m b&iacute; da.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nước Tẩy Trang Bí Đao Cocoon Winter Melon Micellar Water giúp hỗ trợ kiểm soát dầu và ngăn ngừa mụn do bít tắc lỗ chân lông.\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/nuoc-tay-trang-bi-dao-cocoon-lam-sach-giam-dau-140ml-2.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;&nbsp;</p>\r\n\r\n<h2>Độ an to&agrave;n:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Kh&ocirc;ng cồn</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&ocirc;ng sulfate</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&ocirc;ng paraben</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&ocirc;ng dầu kho&aacute;ng</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&ocirc;ng thử nghiệm tr&ecirc;n động vật</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Bảo quản:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Bảo quản nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp hoặc nơi c&oacute; nhiệt độ cao / ẩm ướt.</p>\r\n	</li>\r\n	<li>\r\n	<p>Tr&aacute;nh xa tầm tay trẻ em.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Dung t&iacute;ch:&nbsp;</strong>140ml; 500ml</p>\r\n\r\n<p><strong>Thương hiệu:&nbsp;</strong>Cocoon</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:&nbsp;</strong>Việt Nam</p>\r\n\r\n<p><strong>Sản xuất tại:&nbsp;</strong>Việt Nam</p>\r\n', 1, 8, 11),
(22, 'Nước Tẩy Trang Simple', 'TT02', '60000', 20, '1655721084_TT4.png', '<p><strong><strong><strong>Nước Tẩy Trang Simple Kind To Skin Micellar Cleansing Water&nbsp;</strong></strong></strong>l&agrave; sản phẩm <strong>tẩy trang</strong>&nbsp;d&agrave;nh cho da mặt đến từ <strong>Simple</strong>&nbsp;xuất xứ Anh Quốc. C&ocirc;ng thức cải tiến với c&ocirc;ng nghệ l&agrave;m sạch Micellar chứa h&agrave;ng triệu bong b&oacute;ng Micelles th&ocirc;ng minh gi&uacute;p loại bỏ lớp trang điểm v&agrave; bụi bẩn hiệu quả, l&agrave;m th&ocirc;ng tho&aacute;ng lỗ ch&acirc;n l&ocirc;ng, mang lại cảm gi&aacute;c tươi m&aacute;t cho da sau khi sử dụng, đồng thời cấp ẩm l&ecirc;n đến 4 giờ m&agrave; kh&ocirc;ng để lại dư lượng thừa tr&ecirc;n da.</p>\r\n', '<p><strong><strong>Nước Tẩy Trang&nbsp;</strong>Simple&nbsp;Kind To Skin Micellar Cleansing Water</strong>&nbsp;được mệnh danh l&agrave; &quot;best seller&quot; của&nbsp;Simple&nbsp;kể từ khi ra mắt, được rất rất nhiều t&iacute;n đồ l&agrave;m đẹp ưa th&iacute;ch nhờ v&agrave;o khả năng l&agrave;m sạch, loại bỏ mọi bụi bẩn b&atilde; nhờn c&ugrave;ng lớp makeup m&agrave; kh&ocirc;ng g&acirc;y k&iacute;ch ứng da, kể cả những v&ugrave;ng da nhạy cảm như mắt v&agrave; m&ocirc;i. Mang đến cho bạn l&agrave;n da tươi mới, th&ocirc;ng tho&aacute;ng lỗ ch&acirc;n l&ocirc;ng v&agrave; tr&agrave;n đầy sức sống.</p>\r\n\r\n<h2>Loại da ph&ugrave; hợp:</h2>\r\n\r\n<ul>\r\n	<li>Sản phẩm ph&ugrave; hợp cho mọi loại da, đặc biệt l&agrave;&nbsp;da nhạy cảm.</li>\r\n</ul>\r\n\r\n<h2>Giải ph&aacute;p cho t&igrave;nh trạng da:</h2>\r\n\r\n<ul>\r\n	<li><strong><a href=\"https://hasaki.vn/danh-muc/nhay-cam-kich-ung-c1885.html\" target=\"_blank\">Da nhạy cảm - k&iacute;ch ứng</a></strong></li>\r\n	<li>\r\n	<p><strong><a href=\"https://hasaki.vn/danh-muc/thieu-am-thieu-nuoc-c1883.html\" target=\"_blank\">Da thiếu ẩm, thiếu nước</a></strong></p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Ưu thế nổi bật:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Ứng dụng&nbsp;<strong>c&ocirc;ng nghệ l&agrave;m sạch Micellar</strong>&nbsp;với h&agrave;ng triệu ph&acirc;n tử Micelles th&ocirc;ng minh gi&uacute;p nhẹ nh&agrave;ng l&agrave;m sạch bụi bẩn, dầu thừa tr&ecirc;n da, lớp trang điểm kể cả sản phẩm chống thấm nước.</p>\r\n	</li>\r\n	<li>\r\n	<p>L&agrave;m th&ocirc;ng tho&aacute;ng v&agrave; sạch s&acirc;u lỗ ch&acirc;n l&ocirc;ng m&agrave; vẫn đảm bảo giữ cho da mềm mại, gi&uacute;p l&agrave;n da tươi m&aacute;t v&agrave; gi&uacute;p cấp ẩm cho da l&ecirc;n đến 4h.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&ocirc;ng g&acirc;y nhờn d&iacute;nh, kh&ocirc;ng để lại dư lượng thừa tr&ecirc;n da sau khi tẩy trang.</p>\r\n	</li>\r\n	<li>\r\n	<p>C&ocirc;ng thức được sản xuất&nbsp;với c&aacute;c th&agrave;nh phần an to&agrave;n v&agrave; l&agrave;nh t&iacute;nh cho mọi loại da, kể cả da nhạy cảm bao gồm&nbsp;<strong>Vitamin B3, Vitamin C v&agrave; nước tinh khiết được lọc 3 lần.</strong></p>\r\n\r\n	<ul>\r\n		<li>\r\n		<p><strong>Nước tinh khiết được lọc 3 lần&nbsp;</strong>cực k&igrave; dịu nhẹ v&agrave; an to&agrave;n cho l&agrave;n da nhạy cảm, hỗ trợ l&agrave;m dịu da.</p>\r\n		</li>\r\n		<li>\r\n		<p><strong>Vitamin B3 v&agrave; Vitamin C</strong>&nbsp;c&oacute; khả năng chống oxy h&oacute;a, l&agrave;m dịu da, giảm vi&ecirc;m sưng v&agrave; dưỡng da s&aacute;ng mịn m&agrave;ng.</p>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n	<li>\r\n	<p>KH&Ocirc;NG c&oacute; nước hoa hoặc m&agrave;u nh&acirc;n tạo, KH&Ocirc;NG c&oacute; h&oacute;a chất mạnh, KH&Ocirc;NG cồn, KH&Ocirc;NG x&agrave; ph&ograve;ng, KH&Ocirc;NG paraben v&agrave; KH&Ocirc;NG phtalat.</p>\r\n	</li>\r\n	<li>\r\n	<p>Sản phẩm đ&atilde; được kiểm nghiệm da liễu v&agrave; được x&aacute;c nhận an to&agrave;n cho da, kể cả da nhạy cảm.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Vỏ chai x&aacute;m được l&agrave;m từ 100% nhựa t&aacute;i chế,&nbsp;</strong>th&acirc;n thiện với m&ocirc;i trường.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/nhphuong/Simple_Cleansing_Micellar_Water_Review_2_meitu_5.jpg\" style=\"height:600px; width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/nhphuong/Simple_Cleansing_Micellar_Water_Review_3_meitu_6.jpg\" style=\"height:600px; width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Độ an to&agrave;n:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Sản phẩm kh&ocirc;ng chứa c&aacute;c th&agrave;nh phần c&oacute; khả năng g&acirc;y hại đến l&agrave;n da:</p>\r\n\r\n	<ul>\r\n		<li>\r\n		<p>Kh&ocirc;ng hương liệu, kh&ocirc;ng phẩm m&agrave;u nh&acirc;n tạo</p>\r\n		</li>\r\n		<li>\r\n		<p>Kh&ocirc;ng chứa c&aacute;c h&oacute;a chất mạnh c&oacute; khả năng g&acirc;y k&iacute;ch ứng da</p>\r\n		</li>\r\n		<li>\r\n		<p>Kh&ocirc;ng cồn</p>\r\n		</li>\r\n		<li>\r\n		<p>Kh&ocirc;ng paraben</p>\r\n		</li>\r\n		<li>\r\n		<p>Kh&ocirc;ng&nbsp;phthalates</p>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Bảo quản:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Bảo quản nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp hoặc nơi c&oacute; nhiệt độ cao / ẩm ướt.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đậy k&iacute;n nắp sau khi sử dụng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Dung t&iacute;ch:&nbsp;</strong>200ml</p>\r\n\r\n<p><strong>Thương hiệu:&nbsp;</strong>Simple</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:&nbsp;</strong>Anh Quốc</p>\r\n', 1, 8, 12),
(23, 'Khăn Giấy Tẩy Trang Simple ', 'TT03', '104', 40, '1655721187_TT3.png', '<p><strong>Khăn Giấy Tẩy Trang Simple L&agrave;m Sạch, Dưỡng Ẩm Da 25 Miếng</strong>&nbsp;l&agrave; d&ograve;ng khăn&nbsp;<a href=\"https://hasaki.vn/danh-muc/tay-trang-c37.html\" target=\"_blank\">tẩy trang</a>&nbsp;đến từ&nbsp;<a href=\"https://hasaki.vn/thuong-hieu/simple.html\" target=\"_blank\">thương hiệu mỹ phẩm Simple</a>&nbsp;của Anh, với c&ocirc;ng thức&nbsp;<strong>Micellar Water</strong>&nbsp;chứa c&aacute;c hạt micelles si&ecirc;u nhỏ gi&uacute;p lấy đi c&aacute;c cặn b&atilde; trang điểm, b&atilde; nhờn, bụi bẩn s&acirc;u lỗ ch&acirc;n l&ocirc;ng. Sản phẩm c&ograve;n c&oacute; khả năng cấp ẩm, giữ độ ẩm tự nhi&ecirc;n cho da, kh&ocirc;ng g&acirc;y mất nước, kh&ocirc; căng cho da.</p>\r\n', '<p>Hiện sản phẩm&nbsp;<strong>Khăn Giấy Tẩy Trang Simple Water Boost Hydrating Cleansing Wipes 25 Miếng</strong>&nbsp;đ&atilde; c&oacute; mặt tại&nbsp;<strong>Hasaki.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Khăn Giấy Tẩy Trang Simple Làm Sạch, Dưỡng Ẩm Da 25 Miếng\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/khan-giay-tay-trang-simple-lam-sach-duong-am-da-25-mieng-1.jpg\" style=\"height:800px; width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Loại da ph&ugrave; hợp:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Da kh&ocirc; v&agrave; da nhạy cảm.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Giải ph&aacute;p t&igrave;nh trạng da:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Thường xuy&ecirc;n trang điểm mỗi ng&agrave;y hoặc đi du lịch.</p>\r\n	</li>\r\n	<li>\r\n	<p>Da thiếu nước, thiếu ẩm</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Khăn Giấy Tẩy Trang Simple Làm Sạch, Dưỡng Ẩm Da 25 Miếng Hasaki\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/khan-giay-tay-trang-simple-lam-sach-duong-am-da-25-mieng-2.jpg\" style=\"height:800px; width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Ưu thế nổi bật:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>C&ocirc;ng thức&nbsp;<strong>Micellar Water</strong>&nbsp;chứa v&ocirc; số ph&acirc;n tử Mixen si&ecirc;u nhỏ gi&uacute;p h&uacute;t đi c&aacute;c bụi bẩn, lớp trang điểm, kem chống nắng suốt cả ng&agrave;y.</p>\r\n	</li>\r\n	<li>\r\n	<p>Th&agrave;nh phần&nbsp;<strong>Glycerin</strong>&nbsp;cấp ẩm v&agrave; l&agrave;m mềm da, giảm t&igrave;nh trạng th&ocirc; r&aacute;p, kh&ocirc; căng sau khi tẩy trang.</p>\r\n	</li>\r\n	<li>\r\n	<p>Th&agrave;nh phần&nbsp;<strong>vitamin B5, E</strong>&nbsp;cung cấp ẩm, ngăn sự mất nước v&agrave; hỗ trợ qu&aacute; tr&igrave;nh t&aacute;i tạo, phục hồi da.</p>\r\n	</li>\r\n	<li>\r\n	<p>Sản phẩm l&agrave;m sạch lớp trang điểm v&agrave; bụi bẩn tr&ecirc;n da v&agrave; nhẹ nh&agrave;ng lấy đi tế b&agrave;o chết tr&ecirc;n da.</p>\r\n	</li>\r\n	<li>\r\n	<p>Thiết kế dạng khăn tiện lợi mang theo du lịch hoặc bất cứ đ&acirc;u.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Khăn Giấy Tẩy Trang Simple Water Boost Hydrating Cleansing Wipes Removes Makeup 25 Miếng\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/khan-giay-tay-trang-simple-lam-sach-duong-am-da-25-mieng-4.jpg\" style=\"height:800px; width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Độ an to&agrave;n:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Kh&ocirc;ng chứa cồn biến t&iacute;nh v&agrave; dầu</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&ocirc;ng c&oacute; m&agrave;u</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&ocirc;ng c&oacute; hương thơm nh&acirc;n tạo</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&ocirc;ng chứa hoạt chất độc hại cho da.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n<img alt=\"Khăn Giấy Tẩy Trang Simple Water Boost Hydrating Cleansing Wipes 25 Miếng\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/khan-giay-tay-trang-simple-lam-sach-duong-am-da-25-mieng-3.jpg\" style=\"height:800px; width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Bảo quản:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Nơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t.</p>\r\n	</li>\r\n	<li>\r\n	<p>Tr&aacute;nh &aacute;nh nắng trực tiếp, nơi c&oacute; nhiệt độ cao hoặc ẩm ướt.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đậy nắp k&iacute;n sau khi sử dụng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Dung t&iacute;ch:</strong>&nbsp;200ml</p>\r\n\r\n<p><strong>Thương hiệu:</strong>&nbsp;Simple</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Anh</p>\r\n', 1, 8, 12),
(24, 'Nước Tẩy Trang La Roche-Posay ', 'TT04', '348000', 11, '1655721293_TT3.jpg', '<p><strong>Nước Tẩy Trang La Roche-Posay Micellar Water Ultra Sensitive Skin</strong>&nbsp;đến từ thương hiệu&nbsp;<a href=\"https://hasaki.vn/thuong-hieu/la-roche-posay.html\" target=\"_blank\"><strong>&nbsp;La Roche-Posay</strong></a>&nbsp;của Ph&aacute;p l&agrave; d&ograve;ng sản phẩm&nbsp;d&agrave;nh cho da mặt, v&ugrave;ng mắt v&agrave; m&ocirc;i, ứng dụng c&ocirc;ng nghệ Glyco Micellar gi&uacute;p l&agrave;m sạch s&acirc;u lớp trang điểm v&agrave; bụi bẩn, b&atilde; nhờn tr&ecirc;n da vượt trội m&agrave;&nbsp;vẫn &ecirc;m dịu, kh&ocirc;ng g&acirc;y căng r&aacute;t hay k&iacute;ch ứng da; đồng thời cung cấp độ ẩm, mang đến l&agrave;n da mềm mại &amp; tho&aacute;ng nhẹ sau khi sử dụng.</p>\r\n', '<p><strong><strong>Nước Tẩy Trang&nbsp;</strong>La Roche-Posay Micellar Water Ultra Sensitive Skin</strong>&nbsp;<strong>Cho Da Nhạy Cảm</strong>&nbsp;hiện đ&atilde; c&oacute; mặt tại&nbsp;<a href=\"https://hasaki.vn/\" target=\"_blank\">Hasaki</a>&nbsp;với 3 dung t&iacute;ch:<strong>&nbsp;100ml, 200ml, 400ml.</strong></p>\r\n\r\n<h2>Loại da ph&ugrave; hợp:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Sản phẩm ph&ugrave; hợp cho da thường v&agrave;&nbsp;<a href=\"https://hasaki.vn/danh-muc/da-nhay-cam-c20.html\" target=\"_blank\"><strong>da nhạy cảm</strong></a>.</p>\r\n	</li>\r\n	<li>\r\n	<p>Ph&ugrave; hợp để tẩy trang cho v&ugrave;ng mặt, mắt v&agrave; m&ocirc;i.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Giải ph&aacute;p cho t&igrave;nh trạng da:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p><a href=\"https://hasaki.vn/danh-muc/nhay-cam-kich-ung-c1885.html\" target=\"_blank\"><strong>Da nhạy cảm - k&iacute;ch ứng</strong></a></p>\r\n	</li>\r\n	<li>\r\n	<p>Da thiếu nước - thiếu ẩm</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>C&ocirc;ng dụng:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>C&ocirc;ng nghệ đột ph&aacute;<strong>&nbsp;Glyco Micellar</strong>&nbsp;mang lại hiệu quả l&agrave;m sạch s&acirc;u vượt trội gi&uacute;p lấy đi bụi bẩn, b&atilde; nhờn v&agrave; lớp trang điểm nhưng vẫn an to&agrave;n cho l&agrave;n da nhạy cảm.</p>\r\n	</li>\r\n	<li>\r\n	<p>L&agrave;m sạch đến 99% lớp trang điểm, 70% mascara v&agrave; c&aacute;c hạt bụi si&ecirc;u nhỏ c&oacute; trong kh&oacute;i xe v&agrave; m&ocirc;i trường &ocirc; nhiễm chỉ sau một lượt b&ocirc;ng cotton*.</p>\r\n	</li>\r\n	<li>\r\n	<p>Chứa<strong>&nbsp;nước kho&aacute;ng La Roche-Posay</strong>&nbsp;c&oacute; nồng độ Selenium tự nhi&ecirc;n cao, với đặc t&iacute;nh l&agrave;m dịu da, giảm k&iacute;ch ứng v&agrave; chống oxi h&oacute;a,&nbsp;gi&uacute;p bảo vệ da trước m&ocirc;i trường &ocirc; nhiễm.</p>\r\n	</li>\r\n	<li>\r\n	<p>Cung cấp độ ẩm với&nbsp;<strong>Glycerin,&nbsp;</strong>giảm thiểu ma s&aacute;t g&acirc;y tổn thương da trong qu&aacute; tr&igrave;nh l&agrave;m sạch.</p>\r\n	</li>\r\n	<li>\r\n	<p>Mang lại l&agrave;n da sạch mịn m&agrave;ng, tho&aacute;ng nhẹ sau khi sử dụng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nước Tẩy Trang La Roche-Posay Micellar Water Ultra Sensitive Skin làm sạch bụi mịn PM2.5\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen1/nuoc-tay-trang-la-roche-posay-danh-cho-da-nhay-cam-2.jpg\" style=\"width:800px\" /></p>\r\n\r\n<h2><strong>Độ an to&agrave;n:</strong></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Kh&ocirc;ng paraben / Kh&ocirc;ng chất tạo m&agrave;u / Kh&ocirc;ng cồn / Kh&ocirc;ng chứa x&agrave; ph&ograve;ng.</p>\r\n	</li>\r\n	<li>\r\n	<p>Duy tr&igrave; độ pH tự nhi&ecirc;n của da n&ecirc;n an to&agrave;n với mọi loại da, kể cả da nhạy cảm.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nước Tẩy Trang La Roche-Posay Micellar Water Ultra Sensitive Skin không chứa paraben, chất tạo màu, cồn, xà phòng.\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/nuoc-tay-trang-la-roche-posay-danh-cho-da-nhay-cam-3_1.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Bảo quản:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\r\n	</li>\r\n	<li>\r\n	<p>Để nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đậy nắp k&iacute;n sau khi sử dụng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Hướng dẫn sử dụng Nước Tẩy Trang La Roche-Posay Micellar Water Ultra Sensitive Skin\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/nuoc-tay-trang-la-roche-posay-danh-cho-da-nhay-cam-4_1.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Th&ocirc;ng số sản phẩm:</strong></h2>\r\n\r\n<p><strong>Dung t&iacute;ch:</strong>&nbsp;100ml / 200ml / 400ml</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Ph&aacute;p</p>\r\n\r\n<p><strong>Nơi sản xuất:</strong>&nbsp;Ph&aacute;p</p>\r\n', 1, 8, 9),
(25, 'Nước Tẩy Trang Maybelline ', 'TT05', '130000', 23, '1655721433_TT5.png', '<p>Nước tẩy trang l&agrave; sản phẩm kh&ocirc;ng thể thiếu v&agrave; lu&ocirc;n l&agrave; người bạn đồng h&agrave;nh cho những c&ocirc; n&agrave;ng thường xuy&ecirc;n trang điểm.&nbsp;<strong>Nước Tẩy Trang 4 Trong 1 Kh&ocirc;ng Cồn Cho Mọi Loại Da&nbsp;<strong>Maybelline 4-in-1&nbsp;</strong>Micellar Water</strong>&nbsp;với c&ocirc;ng nghệ ti&ecirc;n tiến được &aacute;p dụng từ&nbsp;<strong>Maybelline New York,</strong>&nbsp;c&ocirc;ng dụng 4in1 gi&uacute;p đơn giản h&oacute;a trong chu tr&igrave;nh tẩy trang của bạn, gi&uacute;p loại bỏ nhanh ch&oacute;ng lớp trang điểm v&agrave; l&agrave;m sạch da một c&aacute;ch dễ d&agrave;ng chỉ trong 1 bước sử dụng, kh&ocirc;ng g&acirc;y cảm gi&aacute;c nhờn r&iacute;t, đồng thời cung cấp độ ẩm gi&uacute;p l&agrave;n da trở n&ecirc;n ẩm mượt, mịn m&agrave;ng v&agrave; sạch ho&agrave;n to&agrave;n.</p>\r\n', '<p>Sản phẩm an to&agrave;n tuyệt đối cho da của bạn ngay cả với l&agrave;n da nhạy cảm. Sản phẩm tẩy trang được khuyến kh&iacute;ch sử dụng trong quy tr&igrave;nh chăm s&oacute;c da ho&agrave;n chỉnh, để mang lại hiệu quả tốt nhất. V&agrave; chắc chắn l&agrave; sản phẩm tẩy trang của nhiều c&ocirc; g&aacute;i tin d&ugrave;ng.</p>\r\n\r\n<p>Hiện&nbsp;<strong>Hasaki</strong>&nbsp;đang c&oacute; c&aacute;c dung t&iacute;ch:&nbsp;<strong>200ml</strong>&nbsp;&amp;&nbsp;<strong>400ml</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/nuoc-tay-trang-maybelline-4-trong-1-khong-con-cho-moi-loai-da-1.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Maybelline</strong>&nbsp;New York&nbsp;l&agrave; c&ocirc;ng ty mỹ phẩm h&agrave;ng đầu tại Mỹ. Khởi nguồn từ việc b&aacute;n mascara qua thư điện tử, mascara&nbsp;<strong>Maybelline</strong>&nbsp;nhanh ch&oacute;ng trở th&agrave;nh m&oacute;n đồ kh&ocirc;ng thể thiếu được của c&aacute;c c&ocirc; g&aacute;i. Ng&agrave;y nay, mascara&nbsp;<strong>Maybelline&nbsp;</strong>l&agrave; mascara được y&ecirc;u th&iacute;ch của nước Mỹ v&agrave; được sự tin tưởng của c&aacute;c chuy&ecirc;n gia trang điểm h&agrave;ng đầu thế giới. Kh&ocirc;ng chỉ dừng lại ở đấy,&nbsp;<strong>Maybelline</strong>&nbsp;c&ograve;n mở rộng cung cấp bộ sưu tập trang điểm về to&agrave;n phương diện. Bạn sẽ lu&ocirc;n t&igrave;m thấy mọi thứ tốt nhất từ&nbsp;<strong>Maybelline</strong>. Sản phẩm đ&atilde; c&oacute; mặt tại&nbsp;<strong>Hasaki</strong>.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/nuoc-tay-trang-maybelline-4-trong-1-khong-con-cho-moi-loai-da-2.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:</strong></p>\r\n\r\n<p>- Sản phẩm kết hợp ho&agrave;n hảo 4in1 chỉ trong 1 bước: Tẩy trang, l&agrave;m sạch, sạch s&acirc;u v&agrave; dưỡng ẩm da.</p>\r\n\r\n<p>- Gi&uacute;p loại bỏ lớp trang điểm&nbsp;kh&ocirc;ng tr&ocirc;i một c&aacute;ch nhẹ nh&agrave;ng, hiệu quả v&agrave; nhanh ch&oacute;ng&nbsp;m&agrave; kh&ocirc;ng lấy đi độ ẩm tự nhi&ecirc;n của da v&agrave; kh&ocirc;ng g&acirc;y k&iacute;ch ứng da. Sử dụng được cho cả mắt, mặt &nbsp;v&agrave; m&ocirc;i.</p>\r\n\r\n<p>- Sản phẩm kh&ocirc;ng chứa paraben, kh&ocirc;ng chứa chất độc hại, an to&agrave;n tuyệt đối cho da.</p>\r\n\r\n<p>Lời khuy&ecirc;n từ chuy&ecirc;n gia:&nbsp;Chỉ c&oacute; sản phẩm tẩy trang chuy&ecirc;n dụng mới loại bỏ hiệu quả lớp trang điểm kh&ocirc;ng lem, kh&ocirc;ng tr&ocirc;i thật nhẹ nh&agrave;ng, kh&ocirc;ng g&acirc;y hại cho l&agrave;n da của bạn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/XuanHuong/nuoc-tay-trang-maybelline-200ml_3_.jpg\" style=\"height:838px; width:750px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Loại da</strong></p>\r\n\r\n<p>D&agrave;nh cho mọi loại da, kể cả l&agrave;n da nhạy cảm.</p>\r\n\r\n<p><strong>Bảo quản:</strong></p>\r\n\r\n<p>Nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t.</p>\r\n\r\n<p>Tr&aacute;nh &aacute;nh nắng trực tiếp, nơi c&oacute; nhiệt độ cao hoặc ẩm ướt.</p>\r\n\r\n<p>Đậy nắp k&iacute;n sau khi sử dụng.</p>\r\n\r\n<p><strong>Dung t&iacute;ch:</strong>&nbsp;200ml &amp; 400ml</p>\r\n\r\n<p><strong>Thương hiệu:&nbsp;</strong>Maybelline</p>\r\n\r\n<p><strong>Xuất Xứ:</strong>&nbsp;Mỹ</p>\r\n\r\n<p><strong>Sản xuất tại:&nbsp;</strong>Trung Quốc</p>\r\n', 1, 8, 10),
(26, 'Dầu Tẩy Trang Cocoon ', 'TT06', '165000', 20, '1655721594_TT6.png', '<p><strong>Dầu Tẩy Trang Cocoon Chiết Xuất Hoa Hồng Rose Cleansing Oil 140ml</strong>&nbsp;l&agrave; d&ograve;ng&nbsp;<strong><a href=\"https://hasaki.vn/danh-muc/tay-trang-c37.html\" target=\"_blank\">dầu tẩy trang</a></strong>&nbsp;từ&nbsp;<a href=\"https://hasaki.vn/thuong-hieu/cocoon.html\" target=\"_blank\"><strong>thương hiệu mỹ phẩm Cocoon</strong></a>&nbsp;của Việt Nam, với c&ocirc;ng thức&nbsp;<strong>dầu hoa hồng</strong>&nbsp;v&agrave;&nbsp;<strong>vitamin E</strong>&nbsp;chọn lọc gi&uacute;p l&agrave;m sạch s&acirc;u mọi lớp trang điểm, bụi bẩn v&agrave; dầu thừa, duy tr&igrave; độ ẩm tr&ecirc;n da m&agrave; kh&ocirc;ng để lại cảm gi&aacute;c nhờn r&iacute;t kh&oacute; chịu.</p>\r\n', '<p>Sử dụng nước tẩy trang hay dầu tẩy trang vẫn l&agrave; c&acirc;u hỏi thắc mắc của nhiều người nhưng n&oacute; c&ograve;n phụ thuộc rất nhiều v&agrave;o nhu cầu của mỗi người. Đối với những bạn thường xuy&ecirc;n trang điểm đặc biệt l&agrave; makeup đậm th&igrave; n&ecirc;n d&ugrave;ng dầu tẩy trang để mang lại hiệu quả sạch tối ưu. Dầu tẩy trang gi&uacute;p n&agrave;ng tẩy trang kỹ hơn, sạch hơn, đồng thời da được cấp th&ecirc;m độ ẩm, kh&ocirc;ng g&acirc;y kh&ocirc; r&aacute;t da. Kh&ocirc;ng những thế, tẩy trang dạng dầu tiết kiệm cho bọn m&igrave;nh kha kha b&ocirc;ng tẩy trang.&nbsp;<strong>Hasaki</strong>&nbsp;giới thiệu đến n&agrave;ng sản phẩm&nbsp;<strong>Dầu Tẩy Trang Cocoon Chiết Xuất Hoa Hồng Rose Cleansing Oil</strong>&nbsp;mới toanh từ nh&agrave;&nbsp;<strong>Cocoon</strong>&nbsp;với khả năng l&agrave;m sạch dễ d&agrave;ng v&agrave; giữ độ pH c&acirc;n bằng cho l&agrave;n da.</p>\r\n\r\n<p><img alt=\"Dầu Tẩy Trang Cocoon Chiết Xuất Hoa Hồng Rose Cleansing Oil\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/dau-tay-trang-cocoon-chiet-xuat-hoa-hong-140ml-1.jpg\" style=\"width:800px\" /></p>\r\n\r\n<h2><strong>Loại da ph&ugrave; hợp:&nbsp;</strong></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Ph&ugrave; hợp cho mọi loại da.&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2><strong>Ưu thế nổi bật:</strong></h2>\r\n\r\n<p><strong>Dầu Tẩy Trang Cocoon Chiết Xuất Hoa Hồng Rose Cleansing Oil</strong>&nbsp;c&oacute; những ưu điểm nổi bật sau:</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>Tinh dầu hoa hồng</strong>&nbsp;gi&uacute;p da săn chắc, ngăn ngừa l&atilde;o h&oacute;a, cấp ẩm s&acirc;u.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Vitamin E</strong>&nbsp;hỗ trợ gi&uacute;p dưỡng ẩm, l&agrave;m mềm da v&agrave; chống oxy h&oacute;a cho l&agrave;n da.</p>\r\n	</li>\r\n	<li>\r\n	<p>Tẩy sạch mọi lớp trang điểm cứng đầu một c&aacute;ch dễ d&agrave;ng.</p>\r\n	</li>\r\n	<li>\r\n	<p>L&agrave;m sạch s&acirc;u lớp trang điểm, bụi bẩn v&agrave; b&atilde; nhờn t&iacute;ch tụ trong lỗ ch&acirc;n l&ocirc;ng.</p>\r\n	</li>\r\n	<li>\r\n	<p>Mang đến l&agrave;n da sạch, mềm mịn m&agrave; kh&ocirc;ng để lại cảm gi&aacute;c nhờn r&iacute;t tr&ecirc;n da.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2><strong>Độ an to&agrave;n:&nbsp;</strong></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Kh&ocirc;ng dầu kho&aacute;ng.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&ocirc;ng thử nghiệm tr&ecirc;n động vật.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&ocirc;ng paraben.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đ&atilde; được kiểm nghiệm bởi chuy&ecirc;n gia da liễu.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2><strong>Bảo quản:</strong></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Nơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t.</p>\r\n	</li>\r\n	<li>\r\n	<p>Tr&aacute;nh &aacute;nh nắng trực tiếp, nơi c&oacute; nhiệt độ cao hoặc nơi ẩm ướt.&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p>Đậy nắp k&iacute;n sau khi sử dụng.&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Dung t&iacute;ch</strong>:&nbsp;140ml&nbsp;</p>\r\n\r\n<p><strong>Thương hiệu</strong>:&nbsp;Cocoon&nbsp;</p>\r\n\r\n<p><strong>Xuất xứ</strong>: Việt Nam</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Việt Nam.</p>\r\n', 1, 8, 11),
(27, 'Tinh Chất Some By Mi AHA-BHA-PHA', 'SR01', '250000', 12, '1655721787_SR3.JPG', '<p><strong>Tinh Chất Some By Mi AHA-BHA-PHA Cho Da Mụn (Mới) 50ml</strong>&nbsp;l&agrave; d&ograve;ng&nbsp;<a href=\"https://hasaki.vn/danh-muc/serum-tinh-chat-duong-da-c75.html\" target=\"_blank\">serum dưỡng da</a>&nbsp;đến từ&nbsp;<a href=\"https://hasaki.vn/thuong-hieu/some-by-mi.html\" target=\"_blank\">thương hiệu mỹ phẩm Some By Mi</a>&nbsp;của H&agrave;n Quốc</p>\r\n', '<p><strong>Tinh Chất Some By Mi AHA-BHA-PHA Cho Da Mụn (Mới) 50ml</strong>&nbsp;l&agrave; d&ograve;ng&nbsp;<a href=\"https://hasaki.vn/danh-muc/serum-tinh-chat-duong-da-c75.html\" target=\"_blank\">serum dưỡng da</a>&nbsp;đến từ&nbsp;<a href=\"https://hasaki.vn/thuong-hieu/some-by-mi.html\" target=\"_blank\">thương hiệu mỹ phẩm Some By Mi</a>&nbsp;của H&agrave;n Quốc, với th&agrave;nh phần chứa&nbsp;73%&nbsp;<strong>tr&agrave;m tr&agrave;,&nbsp;</strong>15% chiết xuất<strong>&nbsp;rau m&aacute;,&nbsp;</strong>10000 ppm<strong>&nbsp;chiết xuất diếp c&aacute;</strong>, 2%<strong>&nbsp;Niacinamide&nbsp;</strong>gi&uacute;p hỗ trợ kh&aacute;ng khuẩn, kh&aacute;ng vi&ecirc;m, l&agrave;m giảm mụn.&nbsp;Đặc biệt sản phẩm chứa 3 th&agrave;nh phần tẩy da chết h&oacute;a học &quot;đ&igrave;nh đ&aacute;m&quot; l&agrave;<strong>&nbsp;AHA - BHA - PHA</strong>&nbsp;sẽ hỗ trợ giảm v&agrave; ngăn ngừa mụn to&agrave;n diện từ mụn đầu đen, đầu trắng, cho đến mụn ẩn v&agrave; mụn sưng vi&ecirc;m, bạn sẽ cảm nhận sự thay đổi của l&agrave;n da sau thời gian ngắn sử dụng sản phẩm.</p>\r\n\r\n<p><img alt=\"Tinh Chất Some By Mi AHA-BHA-PHA Cho Da Mụn (Mới) 50ml \" src=\"https://media.hasaki.vn/wysiwyg/minhchau/tinh-chat-some-by-mi-aha-bha-pha-cho-da-mun-moi-50ml-1.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;&nbsp;</p>\r\n\r\n<p><img alt=\"Tinh Chất Some By Mi AHA-BHA-PHA Cho Da Mụn (Mới)\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/tinh-chat-some-by-mi-aha-bha-pha-cho-da-mun-moi-50ml-4.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;&nbsp;</p>\r\n\r\n<h2>Loại da ph&ugrave; hợp:&nbsp;</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Sản phẩm ph&ugrave; hợp với mọi loại da,&nbsp;khuy&ecirc;n d&ugrave;ng cho da dầu, da mụn, da mẫn cảm.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Giải ph&aacute;p t&igrave;nh trạng da:&nbsp;</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Da dầu - lỗ ch&acirc;n l&ocirc;ng to.&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p>Da thường xuất hiện c&aacute;c vấn đề về&nbsp;<a href=\"https://hasaki.vn/danh-muc/mun-c1877.html\" target=\"_blank\">mụn</a>.</p>\r\n\r\n	<p>&nbsp;</p>\r\n\r\n	<p><img alt=\"Kết cấu Tinh Chất Some By Mi AHA-BHA-PHA Cho Da Mụn (Mới) 50ml \" src=\"https://media.hasaki.vn/wysiwyg/minhchau/tinh-chat-some-by-mi-aha-bha-pha-cho-da-mun-moi-50ml-2.jpg\" style=\"width:800px\" /></p>\r\n\r\n	<p>&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Ưu thế nổi bật:&nbsp;</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Th&agrave;nh phần<strong>&nbsp;AHA - BHA - PHA</strong>&nbsp;tẩy da chết nhẹ nh&agrave;ng đồng thời l&agrave;m giảm v&agrave; ngăn ngừa mụn đầu đen, đầu trắng, mụn ẩn v&agrave; mụn sưng vi&ecirc;m.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Tr&agrave;m Tr&agrave;</strong>&nbsp;hỗ trợ kh&aacute;ng khuẩn, l&agrave;m dịu hỗ trợ l&agrave;m giảm v&agrave; ngăn ngừa sản sinh mụn.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Rau M&aacute;&nbsp;</strong>gi&uacute;p t&aacute;i tạo v&agrave; phục hồi v&ugrave;ng da bị tổn thương do mụn, giảm sẹo th&acirc;m v&agrave; hỗ trợ xoa dịu những nốt mụn đang bị sưng tấy.&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Diếp C&aacute;</strong>&nbsp;gi&uacute;p kiểm so&aacute;t dầu thừa, b&atilde; nhờn v&agrave; c&oacute; khả năng se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng hiệu quả.&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Niacinamide</strong>&nbsp;hỗ trợ l&agrave;m mờ c&aacute;c vết th&acirc;m mụn, da kh&ocirc;ng đều m&agrave;u mang lại l&agrave;n da s&aacute;ng mịn, rạng ngời.</p>\r\n	</li>\r\n	<li>\r\n	<p>L&agrave;m sạch bụi bẩn, b&atilde; nhờn v&agrave; da chết tr&ecirc;n bề mặt da, s&acirc;u trong lỗ ch&acirc;n l&ocirc;ng đồng thời hỗ trợ giảm th&acirc;m, l&agrave;m s&aacute;ng v&agrave; đều m&agrave;u da.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kết cấu dạng lỏng nhẹ, dễ d&agrave;ng thẩm thấu s&acirc;u v&agrave;o da.&nbsp;</p>\r\n\r\n	<p>&nbsp;</p>\r\n\r\n	<p><img alt=\"Tinh Chất Some By Mi AHA-BHA-PHA 30 Days Miracle Serum Light 50ml\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/tinh-chat-some-by-mi-aha-bha-pha-cho-da-mun-moi-50ml-3.jpg\" style=\"width:800px\" /></p>\r\n\r\n	<p>&nbsp;</p>\r\n\r\n	<p><img alt=\"Tinh Chất Some By Mi AHA-BHA-PHA 30 Days Miracle Serum Light\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/tinh-chat-some-by-mi-aha-bha-pha-cho-da-mun-moi-50ml-5.jpg\" style=\"width:800px\" /></p>\r\n\r\n	<p>&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Độ an to&agrave;n:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Sản phẩm&nbsp;kh&ocirc;ng chứa Paraben, chất tạo m&agrave;u, dầu kho&aacute;ng v&agrave; những h&oacute;a chất g&acirc;y hại.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Bảo quản:&nbsp;</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Nơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t.&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p>Tr&aacute;nh &aacute;nh nắng trực tiếp, nơi c&oacute; nhiệt độ cao hoặc ẩm ướt.&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p>Đậy nắp k&iacute;n sau khi sử dụng.&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Dung t&iacute;ch:&nbsp;</strong>50ml</p>\r\n\r\n<p><strong>Thương hiệu:&nbsp;</strong>Some By Mi&nbsp;</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:&nbsp;</strong>H&agrave;n Quốc</p>\r\n\r\n<p><strong>Sản xuất tại:&nbsp;</strong>H&agrave;n Quốc.</p>\r\n', 1, 7, 7),
(28, 'Nước Cân Bằng Simple ', 'TN01', '100000', 35, '1655721916_toner2.png', '<p><strong>Nước C&acirc;n Bằng&nbsp;L&agrave;m Dịu Da &amp; Cấp Ẩm<strong>&nbsp;</strong><strong>Simple&nbsp;</strong>Kind to Skin Soothing Facial Toner&nbsp;</strong>đến từ&nbsp;<a href=\"https://hasaki.vn/thuong-hieu/simple.html\" target=\"_blank\"><strong>thương hiệu chăm s&oacute;c da Simple</strong></a>&nbsp;xuất xứ Anh Quốc l&agrave; sản phẩm&nbsp;<a href=\"https://hasaki.vn/danh-muc/toner-c1857.html\" target=\"_blank\"><strong>Toner</strong></a>&nbsp;kh&ocirc;ng chứa cồn được thiết kế ph&ugrave; hợp cho l&agrave;n da nhạy cảm v&agrave; dễ nổi mụn, gi&uacute;p l&agrave;m sạch s&acirc;u v&agrave; c&acirc;n bằng độ pH, cung cấp độ ẩm dịu nhẹ cho l&agrave;n da, mang lại cảm gi&aacute;c tươi m&aacute;t v&agrave; sảng kho&aacute;i sau khi sử dụng.</p>\r\n', '<p>&nbsp;</p>\r\n\r\n<h2>Loại da ph&ugrave; hợp:</h2>\r\n\r\n<ul>\r\n	<li>Sản phẩm ph&ugrave; hợp cho mọi loại da, đặc biệt l&agrave; da nhạy cảm.</li>\r\n</ul>\r\n\r\n<h2>Giải ph&aacute;p cho t&igrave;nh trạng da:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p><a href=\"https://hasaki.vn/danh-muc/nhay-cam-kich-ung-c1885.html\" target=\"_blank\">Da nhạy cảm, dễ k&iacute;ch ứng</a></p>\r\n	</li>\r\n	<li>\r\n	<p><a href=\"https://hasaki.vn/danh-muc/thieu-am-thieu-nuoc-c1883.html\" target=\"_blank\">Da thiếu ẩm, thiếu nước</a></p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Ưu thế nổi bật:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Chứa&nbsp;<strong>Vitamin B5 (Panthenol)</strong>&nbsp;v&agrave;&nbsp;<strong>Allantoin</strong>&nbsp;gi&uacute;p l&agrave;m dịu da, giảm mẩn đỏ, k&iacute;ch ứng v&agrave; hỗ trợ phục hồi l&agrave;n da khỏe mạnh.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Chiết xuất c&acirc;y Phỉ (Witch Hazel)</strong>&nbsp;gi&uacute;p l&agrave;m sạch s&acirc;u, điều tiết lượng dầu thừa v&agrave; hỗ trợ thu nhỏ lỗ ch&acirc;n l&ocirc;ng, đồng thời ngăn ngừa vi&ecirc;m nhiễm, kh&aacute;ng khuẩn cho da mụn.</p>\r\n	</li>\r\n	<li>\r\n	<p>L&yacute; tưởng để sử dụng sau bước rửa mặt v&agrave; trước kem dưỡng ẩm v&agrave;o buổi s&aacute;ng &amp; tối để gi&uacute;p loại bỏ bụi bẩn v&agrave; cặn trang điểm c&ograve;n s&oacute;t lại tr&ecirc;n da.</p>\r\n	</li>\r\n	<li>\r\n	<p>C&acirc;n bằng độ pH, duy tr&igrave; l&agrave;n da săn chắc v&agrave; tươi mới.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kết cấu dạng lỏng nhẹ như nước, mang đến cảm gi&aacute;c tươi m&aacute;t v&agrave; sảng kho&aacute;i sau khi sử dụng.</p>\r\n	</li>\r\n	<li>\r\n	<p>Vỏ chai được l&agrave;m từ 100% nhựa t&aacute;i chế, th&acirc;n thiện với m&ocirc;i trường.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nước hoa hồng Simple Kind to Skin Soothing Facial Toner cân bằng da, làm dịu &amp; cấp ẩm tức thì.\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen1/nuoc-hoa-hong-simple-lam-diu-can-bang-do-am-da-200ml-1.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;<img alt=\"Nước hoa hồng Simple làm dịu &amp; cấp ẩm sâu cho da mịn màng\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen1/nuoc-hoa-hong-simple-lam-diu-can-bang-do-am-da-200ml-3.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Hướng dẫn sử dụng Nước Hoa Hồng Simple Kind to Skin Soothing Facial Toner 200ml\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen1/nuoc-hoa-hong-simple-lam-diu-can-bang-do-am-da-200ml-4.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Độ an to&agrave;n:</h2>\r\n\r\n<ul>\r\n	<li>Kh&ocirc;ng chứa hương liệu, chất tạo m&agrave;u, cồn, dầu kho&aacute;ng, th&agrave;nh phần từ động vật, h&oacute;a chất c&oacute; hại, do đ&oacute; ho&agrave;n to&agrave;n an to&agrave;n v&agrave; dịu nhẹ cho l&agrave;n da nhạy cảm.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nước Hoa Hồng Simple Kind to Skin Soothing Facial Toner không chứa các thành phần gây hại\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen1/nuoc-hoa-hong-simple-lam-diu-can-bang-do-am-da-200ml-2.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Bảo quản:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\r\n	</li>\r\n	<li>\r\n	<p>Để nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đậy nắp k&iacute;n sau khi sử dụng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Th&ocirc;ng số sản phẩm:</h2>\r\n\r\n<p><strong>Dung t&iacute;ch:&nbsp;</strong>200ml</p>\r\n\r\n<p><strong>Thương hiệu:&nbsp;</strong>Simple</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Anh</p>\r\n', 1, 7, 12);
INSERT INTO `tbl_sanpham` (`id_sanpham`, `tensanpham`, `masp`, `giasp`, `soluong`, `hinhanh`, `tomtat`, `noidung`, `tinhtrang`, `id_danhmuc`, `id_hieusp`) VALUES
(29, 'Nước Cân Bằng Cocoon Chiết Xuất Bí Đao ', 'TN02', '270000', 32, '1655722076_toner1.png', '<p>B&iacute; đao l&agrave; loại quả v&ocirc; c&ugrave;ng quen thuộc trong đời sống của người d&acirc;n Việt Nam. Thế nhưng kh&ocirc;ng chỉ l&agrave; một loại quả m&aacute;t l&agrave;nh gi&uacute;p giải nhiệt cơ thể trong những ng&agrave;y h&egrave; n&oacute;ng bức, b&iacute; đao c&ograve;n c&oacute; nhiều lợi &iacute;ch cho l&agrave;n da của ch&uacute;ng ta.&nbsp;</p>\r\n', '<p>Trong quả b&iacute; đao rất gi&agrave;u những th&agrave;nh phần chống oxy h&oacute;a v&agrave; kh&aacute;ng vi&ecirc;m như Vitamin A v&agrave; C, gi&uacute;p hỗ trợ l&agrave;m giảm sưng vi&ecirc;m mụn v&agrave; ngăn ngừa c&aacute;c tổn thương da do gốc tự do, đồng thời dưỡng da s&aacute;ng mịn hồng h&agrave;o. Thường xuy&ecirc;n sử dụng b&iacute; đao sẽ gi&uacute;p tăng sức đề kh&aacute;ng cho l&agrave;n da, l&agrave;m mềm v&agrave; cung cấp dưỡng chất nu&ocirc;i da khỏe l&ecirc;n mỗi ng&agrave;y.giới thiệu đến c&aacute;c bạn một sản phẩm chăm s&oacute;c da độc đ&aacute;o được chiết xuất từ th&agrave;nh phần b&iacute; đao đến từ thương hiệu mỹ phẩm Việt Nam<strong>&nbsp;COCOON&nbsp;&ndash; Nước B&iacute; Đao C&acirc;n Bằng Da Cocoon Winter Melon Toner.&nbsp;</strong>L&agrave; bước tiếp nối sau khi d&ugrave;ng&nbsp;<strong>Gel B&iacute; Đao Rửa Mặt Cocoon Winter Melon Cleanser</strong>, nước b&iacute; đao Cocoon với c&ocirc;ng thức kh&ocirc;ng chứa cồn c&ugrave;ng th&agrave;nh phần gi&agrave;u c&aacute;c chiết xuất tự nhi&ecirc;n bao gồm chiết xuất b&iacute; đao, chiết xuất rau m&aacute; v&agrave; tinh dầu tr&agrave;m tr&agrave; sẽ gi&uacute;p c&acirc;n bằng độ pH cho da, l&agrave;m sạch s&acirc;u lỗ ch&acirc;n l&ocirc;ng, giảm dầu thừa v&agrave; mụn ẩn dưới da hiệu quả. Ngo&agrave;i ra sản phẩm c&ograve;n được bổ sung th&ecirc;m Vitamin B3, HA v&agrave; chiết xuất cam thảo cung cấp độ ẩm gi&uacute;p da lu&ocirc;n mịn m&agrave;ng v&agrave; giảm thiểu c&aacute;c vết đỏ tr&ecirc;n da.&nbsp;<strong>Nước B&iacute; Đao C&acirc;n Bằng Da Cocoon Winter Melon Toner&nbsp;</strong>sẽ l&agrave; sự lựa chọn tuyệt vời cho những l&agrave;n da dầu bị mụn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nước Bí Đao Cân Bằng Da Cocoon Winter Melon Toner \" src=\"https://media.hasaki.vn/wysiwyg/minhchau/gel-rua-mat-cocoon-chiet-xuat-bi-dao-1.jpg\" style=\"height:800px; width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/nuoc-bi-dao-can-bang-da-cocoon-140ml-1.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/nuoc-bi-dao-can-bang-da-cocoon-140ml-2.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Cocoon</strong>&nbsp;l&agrave;&nbsp;thương hiệu mỹ phẩm Việt Nam trực thuộc Nature Store chuy&ecirc;n cung cấp c&aacute;c sản phẩm l&agrave;m đẹp được chiết xuất 100% từ thi&ecirc;n nhi&ecirc;n. C&aacute;c sản phẩm&nbsp;<strong>Cocoon</strong>&nbsp;được sản xuất từ c&aacute;c nguy&ecirc;n liệu quen thuộc trong c&aacute;c bữa ăn h&agrave;ng ng&agrave;y của người Việt như: B&iacute; đao, mật ong, nghệ, rượu nếp,&hellip; v&agrave; đ&atilde; được thử nghiệm chứng thực l&agrave; hiệu quả v&agrave; an to&agrave;n với người sử dụng, gi&uacute;p mang lại cho bạn một l&agrave;n da, một m&aacute;i t&oacute;c lu&ocirc;n khỏe mạnh, trẻ trung v&agrave; tr&agrave;n đầy sức sống.&nbsp;<strong>Cocoon</strong>&nbsp;&nbsp;cam kết sử dụng 100% nguy&ecirc;n liệu c&oacute; nguồn gốc r&otilde; r&agrave;ng, 100% mỹ phẩm thuần chay v&agrave; 100% kh&ocirc;ng bao giờ thử nghiệm tr&ecirc;n động vật. Nhờ vậy, c&aacute;c sản phẩm của&nbsp;<strong>Cocoon</strong>&nbsp;l&agrave; b&iacute; quyết l&agrave;m đẹp hiệu quả được nhiều người tin d&ugrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/nuoc-bi-dao-can-bang-da-cocoon-140ml-4.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/nuoc-bi-dao-can-bang-da-cocoon-140ml-3.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>Th&agrave;nh phần hoạt chất ch&iacute;nh:</strong></h3>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>B&iacute; đao:</strong>&nbsp;Theo s&aacute;ch y học cổ truyền, b&iacute; đao c&oacute; đặc t&iacute;nh l&agrave;m m&aacute;t, l&agrave;m giảm nhiệt, kh&aacute;ng vi&ecirc;m v&agrave; diệt khuẩn gi&uacute;p điều trị mụn trứng c&aacute;, mụn vi&ecirc;m.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Rau m&aacute;:</strong>&nbsp;C&aacute;c hợp chất sinh học như axit asiatic, asiaticoside, axit madecassic v&agrave; madecassoside c&oacute; trong rau m&aacute; l&agrave; những chất ch&iacute;nh gi&uacute;p tăng sinh collagen cho l&agrave;n da, kh&aacute;ng vi&ecirc;m, l&agrave;m dịu c&aacute;c vết đỏ v&agrave; ngăn ngừa k&iacute;ch ứng da.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Tinh dầu tr&agrave;m tr&agrave;:</strong>&nbsp;hỗ trợ giảm mụn trứng c&aacute;, vết thương, c&ocirc;n tr&ugrave;ng cắn v&agrave; nhiễm tr&ugrave;ng nấm men.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Chiết xuất cam thảo:</strong>&nbsp;ngăn ngừa dị ứng, kh&aacute;ng vi&ecirc;m v&agrave; chống oxy h&oacute;a mạnh mẽ.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Vitamin B3 (Niacinamide):</strong>&nbsp;hỗ trợ giảm mụn hiệu quả, kiểm so&aacute;t lượng dầu thừa v&agrave; l&agrave;m giảm sự xuất hiện của lỗ ch&acirc;n l&ocirc;ng to.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>HA (Hyaluronic acid):</strong>&nbsp;Gi&uacute;p dưỡng ẩm, giữ ẩm cho da v&agrave; l&agrave;m mờ nếp nhăn, ngăn ngừa l&atilde;o h&oacute;a.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h3><strong>Loại da ph&ugrave; hợp:</strong></h3>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Sản phẩm ph&ugrave; hợp với mọi loại da, đặc biệt l&agrave;&nbsp;<a href=\"https://hasaki.vn/danh-muc/da-dau-c90.html\" target=\"_blank\"><strong>da dầu</strong></a>&nbsp;v&agrave; mụn ẩn.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h3><strong>C&ocirc;ng dụng:</strong></h3>\r\n\r\n<ul>\r\n	<li>\r\n	<p>L&agrave;m sạch da, trả lại độ pH l&yacute; tưởng cho da sau rửa mặt.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kiểm so&aacute;t lượng dầu tiết ra tr&ecirc;n da, ngăn ngừa mụn ẩn h&igrave;nh th&agrave;nh.</p>\r\n	</li>\r\n	<li>\r\n	<p>Cấp ẩm cho da lu&ocirc;n mịn m&agrave;ng, giảm thiểu c&aacute;c vết đỏ tr&ecirc;n da.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h3><strong>Độ an to&agrave;n:</strong></h3>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Kh&ocirc;ng chứa cồn (Alclhol free)</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&ocirc;ng Paraben (Paraben free)</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&ocirc;ng hương liệu (Fragrance free)</p>\r\n	</li>\r\n</ul>\r\n\r\n<h3><strong>Bảo quản:</strong></h3>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t.</p>\r\n	</li>\r\n	<li>\r\n	<p>Tr&aacute;nh &aacute;nh nắng trực tiếp, nơi c&oacute; nhiệt độ cao hoặc ẩm ướt.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đậy nắp k&iacute;n sau khi sử dụng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Thương hiệu:</strong>&nbsp;Cocoon</p>\r\n\r\n<p><strong>Dung t&iacute;ch:</strong>&nbsp;140ml; 310ml</p>\r\n\r\n<p><strong>Xuất Xứ:&nbsp;</strong>Việt Nam</p>\r\n', 1, 7, 11),
(30, 'Serum La Roche-Posay Phục Hồi ', 'SR02', '779000', 15, '1655722303_SR2.png', '<p><strong>Tinh Chất La Roche-Posay Hyalu B5 Serum&nbsp;</strong>l&agrave; d&ograve;ng <strong>SERUM</strong>&nbsp;chuy&ecirc;n biệt của&nbsp;<a href=\"https://hasaki.vn/thuong-hieu/la-roche-posay.html\" target=\"_blank\"><strong>&nbsp;La Roche-Posay</strong></a>&nbsp;với c&ocirc;ng thức&nbsp;gi&uacute;p t&aacute;i tạo &amp; phục hồi da độc đ&aacute;o gồm hai ph&acirc;n tử Hyaluronic Acid nguy&ecirc;n chất, vitamin B5 v&agrave; Madecassoside, gi&uacute;p l&agrave;n da căng mịn, trở n&ecirc;n săn chắc &amp; đ&agrave;n hồi hơn, đồng thời gi&uacute;p phục hồi h&agrave;ng r&agrave;o bảo vệ da v&agrave; k&iacute;ch hoạt t&aacute;i tạo da.</p>\r\n', '<h2>Loại da ph&ugrave; hợp:</h2>\r\n\r\n<ul>\r\n	<li>Sản phẩm th&iacute;ch hợp với với mọi loại da, kể cả da nhạy cảm</li>\r\n</ul>\r\n\r\n<h2>Giải ph&aacute;p cho t&igrave;nh trạng da:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Da kh&ocirc;,<a href=\"https://hasaki.vn/danh-muc/thieu-am-thieu-nuoc-c1883.html\" target=\"_blank\">&nbsp;</a>thiếu ẩm,&nbsp;mất đi sự đ&agrave;n hồi &amp; độ săn chắc.</p>\r\n	</li>\r\n	<li>\r\n	<p>Da nhạy cảm, k&iacute;ch ứng.</p>\r\n	</li>\r\n	<li>\r\n	<p>Da cần phục hồi sau mụn.</p>\r\n	</li>\r\n	<li>\r\n	<p>Th&iacute;ch hợp sử dụng sau c&aacute;c liệu tr&igrave;nh chăm s&oacute;c thẩm mỹ.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Tinh Chất La Roche-Posay Hyalu B5 Serum hiện đã có mặt tại Hasaki\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/tinh-chat-la-roche-posay-phuc-hoi-do-am-san-chac-da-30ml-4.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Ưu thế nổi bật:</strong></h2>\r\n\r\n<ul>\r\n	<li><strong>HYALURONIC ACIDS DUO</strong>&nbsp;nguy&ecirc;n chất với k&iacute;ch thước kh&aacute;c nhau gi&uacute;p cải thiện nếp nhăn v&agrave; vẻ rạng rỡ của da:\r\n\r\n	<ul>\r\n		<li>\r\n		<p>K&iacute;ch thước ph&acirc;n tử to &ndash; dưỡng ẩm mờ r&atilde;nh nhăn tr&ecirc;n bề mặt.</p>\r\n		</li>\r\n		<li>\r\n		<p>K&iacute;ch thước ph&acirc;n tử nhỏ &ndash; thấm s&acirc;u v&agrave; l&agrave;m căng mọng tầng dưới da.</p>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n	<li>L&agrave;m dịu v&agrave; hỗ trợ phục hồi da với&nbsp;<strong>Vitamin B5</strong>, đồng thời&nbsp;k&iacute;ch th&iacute;ch sự t&aacute;i tạo v&agrave; sức đề kh&aacute;ng của da, mang lại l&agrave;n da khỏe mạnh.</li>\r\n	<li>\r\n	<p>L&agrave;m đầy c&aacute;c nếp nhăn bằng c&aacute;ch l&agrave;m săn chắc da từ b&ecirc;n trong với&nbsp;<strong>Madecassoside</strong>. Đồng thời, th&agrave;nh phần&nbsp;n&agrave;y c&ograve;n gi&uacute;p l&agrave;m dịu, k&iacute;ch th&iacute;ch hiệu quả của Vitamin B5 &amp; Hyaluronic Acid.</p>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>\r\n	<p>L&agrave;n da được phục hồi, mang lại cảm gi&aacute;c căng mịn v&agrave; tr&ocirc;ng rạng rỡ hơn. Nếp nhăn tr&ecirc;n da đồng thời được cải thiện.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kết cấu dạng kem-gel lỏng, nhẹ, thẩm thấu nhanh, kh&ocirc;ng g&acirc;y nhờn r&iacute;t.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2><strong>Độ an to&agrave;n:</strong></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Kh&ocirc;ng g&acirc;y k&iacute;ch ứng da.</p>\r\n	</li>\r\n	<li>\r\n	<p>Được kiểm nghiệm bởi b&aacute;c sĩ da liễu tr&ecirc;n l&agrave;n da nhạy cảm.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&ocirc;ng g&acirc;y nh&acirc;n mụn trứng c&aacute;.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Tinh Chất La Roche-Posay Hyalu B5 Serum làm da rạng rỡ và phục hồi nhanh chóng\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/tinh-chat-la-roche-posay-phuc-hoi-do-am-san-chac-da-30ml-5.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Chu trình chăm sóc da với Tinh Chất La Roche-Posay Hyalu B5 Serum\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/tinh-chat-la-roche-posay-phuc-hoi-do-am-san-chac-da-30ml-6.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Bảo quản:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\r\n	</li>\r\n	<li>\r\n	<p>Để nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đậy nắp k&iacute;n sau khi sử dụng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Dung t&iacute;ch:&nbsp;</strong>30ml</p>\r\n\r\n<p><strong>Thương hiệu:</strong>&nbsp;La Roche-Posay</p>\r\n\r\n<p><strong>Xuất xứ:&nbsp;</strong>Ph&aacute;p</p>\r\n\r\n<p><strong>Sản xuất tại:&nbsp;</strong>Ph&aacute;p</p>\r\n', 1, 7, 9),
(31, 'Tinh Chất La Roche-Posay retinol', 'SR03', '889000', 16, '1655722534_SR1.png', '<p><strong>Tinh Chất La Roche-Posay Giảm Th&acirc;m N&aacute;m &amp; L&agrave;m Đều M&agrave;u Da 30ml</strong>&nbsp;l&agrave; sản phẩm serum&nbsp;đến từ thương hiệu La Roche-Posay&nbsp;của Ph&aacute;p, chứa c&aacute;c th&agrave;nh phần ưu việt trong dưỡng da với&nbsp;<strong>0.3% Retinol</strong>, c&ugrave;ng<strong>&nbsp;2% vitamin B3 (Niacinamide)</strong>&nbsp;v&agrave;&nbsp;<strong>10% Glycerin</strong>&nbsp;gi&uacute;p l&agrave;m giảm nếp nhăn, hạn chế c&aacute;c dấu hiệu l&atilde;o ho&aacute; da đồng thời l&agrave;m s&aacute;ng v&agrave; đều m&agrave;u da, ngăn h&igrave;nh th&agrave;nh c&aacute;c đốm sắc tố tối m&agrave;u da. Đặc biệt sản phẩm với c&ocirc;ng thức an to&agrave;n tr&ecirc;n mọi l&agrave;n da, kể cả da nhạy cảm.</p>\r\n', '<p><strong>Tinh Chất La Roche-Posay Retinol B3 Serum 30ml</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Loại da ph&ugrave; hợp:&nbsp;</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Sản phẩm ph&ugrave; hợp với mọi loại da.&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Giải ph&aacute;p t&igrave;nh trạng da:&nbsp;</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Da l&atilde;o h&oacute;a nếp nhăn&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p>Da xỉn m&agrave;u v&agrave; th&acirc;m sạm.&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p>Da c&oacute; c&aacute;c đốm sắc tố như n&aacute;m, t&agrave;n nhang...&nbsp;</p>\r\n\r\n	<p>&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><img alt=\"Kết cấu Tinh Chất La Roche-Posay Giảm Thâm Nám &amp; Làm Đều Màu Da 30ml\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/tinh-chat-la-roche-posay-giam-tham-nam-lam-deu-mau-da-30ml-3.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Bao bì Tinh Chất La Roche-Posay Giảm Thâm Nám &amp; Làm Đều Màu Da 30ml\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/tinh-chat-la-roche-posay-giam-tham-nam-lam-deu-mau-da-30ml-2.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Ưu thế nổi bật:&nbsp;</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>Retinol 0.3% tinh khiết</strong>&nbsp;c&oacute; khả năng l&agrave;m mờ c&aacute;c nếp nhăn tr&ecirc;n da, l&agrave;m đều m&agrave;u da, k&iacute;ch th&iacute;ch t&aacute;i tạo da, tăng cường sản sinh collagen mang đến l&agrave;n da đ&agrave;n hồi, săn chắc v&agrave; căng mịn.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Vitamin B3</strong>&nbsp;gi&uacute;p l&agrave;m mờ c&aacute;c đốm n&acirc;u, loại bỏ th&acirc;m sạm đem đến l&agrave;n da s&aacute;ng mịn, tươi trẻ.&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Glycerin</strong>&nbsp;cung cấp độ ẩm, l&agrave;m mềm da tr&aacute;nh t&igrave;nh trạng qu&aacute; bong tr&oacute;c tr&ecirc;n da, v&agrave; nu&ocirc;i dưỡng l&agrave;n da khoẻ mạnh.&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p>Hỗ trợ cải thiện lỗ ch&acirc;n l&ocirc;ng, l&agrave;m th&ocirc;ng tho&aacute;ng, ngăn b&iacute;t tắc lỗ ch&acirc;n l&ocirc;ng sản sinh mụn.</p>\r\n\r\n	<p>&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><img alt=\"Tinh Chất La Roche-Posay Retinol B3 Serum\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/tinh-chat-la-roche-posay-giam-tham-nam-lam-deu-mau-da-30ml-5.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Bảo quản:&nbsp;</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Nơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t.&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p>Tr&aacute;nh &aacute;nh nắng trực tiếp, nơi c&oacute; nhiệt độ cao hoặc ẩm ướt.&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p>Đậy nắp k&iacute;n sau khi sử dụng.&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Dung t&iacute;ch:</strong>&nbsp;30ml&nbsp;</p>\r\n\r\n<p><strong>Thương hiệu:</strong>&nbsp;La Roche - Posay&nbsp;</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Ph&aacute;p&nbsp;</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Ph&aacute;p.</p>\r\n', 1, 7, 9),
(32, 'Che Khuyết Điểm Maybelline', 'CKD01', '110000', 14, '1655722693_CKD1.png', '<p><strong>Maybelline New York</strong>&nbsp;l&agrave; c&ocirc;ng ty mỹ phẩm h&agrave;ng đầu tại Mỹ. Khởi nguồn từ việc b&aacute;n mascara qua thư điện tử, mascara&nbsp;<strong>Maybelline</strong>&nbsp;nhanh ch&oacute;ng trở th&agrave;nh m&oacute;n đồ kh&ocirc;ng thể thiếu được của c&aacute;c c&ocirc; g&aacute;i. Ng&agrave;y nay,&nbsp;Maybelline l&agrave; mascara được y&ecirc;u th&iacute;ch của nước Mỹ v&agrave; được sự tin tưởng của c&aacute;c chuy&ecirc;n gia trang điểm h&agrave;ng đầu thế giới.</p>\r\n', '<p>Kh&ocirc;ng chỉ dừng lại ở đấy,&nbsp;<strong>Maybelline&nbsp;</strong>c&ograve;n mở rộng cung cấp bộ sưu tập trang điểm về to&agrave;n phương diện. Bạn sẽ lu&ocirc;n t&igrave;m thấy mọi thứ tốt nhất từ<strong><a href=\"https://hasaki.vn/thuong-hieu/maybelline.html\" target=\"_blank\">&nbsp;Maybelline</a>.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/nhphuong/PhuongSmall/Maybelline_New_York.jpg\" style=\"height:200px; width:800px\" /></strong></p>\r\n\r\n<p><strong><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/Dai/che-khuyet-diem-min-li-fit-me-maybellinek.jpg\" style=\"height:600px; width:600px\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Maybelline Fit Me Concealer</strong>&nbsp;l&agrave; kem che khuyết điểm kinh điển lu&ocirc;n nằm trong top những sản phẩm best seller của<strong>&nbsp;Maybelline.</strong>&nbsp;Với kết cấu dạng lỏng, nhiều t&ocirc;ng m&agrave;u, độ che phủ kh&aacute; cao nhưng chất kem lại v&ocirc; c&ugrave;ng mỏng mịn, dễ d&agrave;ng t&aacute;n đều v&agrave; b&aacute;m v&agrave;o da, che giấu tất cả những khuyết điểm khiến bạn kh&ocirc;ng h&agrave;i l&ograve;ng tr&ecirc;n gương mặt.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;<img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/nhphuong/PhuongSmall/che-khuyet-diem-maybelline-6-8ml-1.jpg\" style=\"height:492px; width:500px\" /></p>\r\n\r\n<p><br />\r\n&nbsp;</p>\r\n\r\n<p>Bất kể mụn, vết n&aacute;m, t&agrave;n nhang, hay những nếp nhăn v&agrave; quầng th&acirc;m tr&ecirc;n đ&ocirc;i mắt, đều sẽ được che đậy kh&eacute;o l&eacute;o v&agrave; biến mất trong nhiều giờ. Được chế tạo với c&ocirc;ng thức kh&ocirc;ng dầu, kh&ocirc;ng hương liệu, đ&atilde; được c&aacute;c b&aacute;c sĩ da liễu kiểm nghiệm n&ecirc;n bạn c&oacute; thể y&ecirc;n t&acirc;m sử dụng kem che khuyết điểm&nbsp;<strong>Maybelline Fit me Concealer&nbsp;</strong>cho da nhạy cảm v&agrave; da dầu, da mụn v&agrave; cũng kh&ocirc;ng lo về vấn đề dầu thừa tr&ecirc;n gương mặt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/Dai/che-khuyet-diem-min-li-fit-me-maybellinet.jpg\" style=\"height:600px; width:600px\" />&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Điểm cộng của&nbsp;<strong>Fit me Concealer</strong>&nbsp;so với c&aacute;c loại kem che khuyết điểm kh&aacute;c đ&oacute; ch&iacute;nh l&agrave;&nbsp;n&oacute; c&oacute; thể che giấu c&aacute;c khuyết điểm tr&ecirc;n da v&ocirc; c&ugrave;ng tự nhi&ecirc;n.&nbsp;Kh&ocirc;ng dầy cộm, kh&ocirc;ng g&acirc;y hằn,&nbsp;<strong>Maybelline Fit me Concealer</strong>&nbsp;tr&ecirc;n da nhẹ t&ecirc;nh. Đ&ocirc;i mắt cũng s&aacute;ng l&ecirc;n khi quầng th&acirc;m v&agrave; những nếp nhăn đ&atilde; được kem che khuyết điểm&nbsp;<strong>Maybelline Fit me Concealer</strong>&nbsp;che đậy.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/nhphuong/PhuongSmall/che-khuyet-diem-maybelline-6-8ml-3.jpg\" style=\"height:372px; width:700px\" /></p>\r\n', 1, 6, 10),
(33, 'Bút Cushion Che Khuyết Điểm Maybelline 120 ', 'CKD02', '129000', 30, '1655722815_CKD02.png', '<p><strong>Che Khuyết Điểm&nbsp;<strong>Maybelline&nbsp;</strong>Instant Age Rewind Treatment Concealer&nbsp;</strong>ch&iacute;nh l&agrave;&nbsp;&ldquo;cứu tinh&rdquo; cho những đ&ocirc;i mắt &ldquo;gấu tr&uacute;c&rdquo; cần che đi quầng th&acirc;m mệt mỏi v&agrave; bọng mắt &ldquo;xấu x&iacute;&rdquo; khi c&oacute; thể che phủ những khuyết điểm chỉ trong gi&acirc;y l&aacute;t v&agrave; giữ kết quả &ldquo;tuyệt vời&rdquo; ấy trong nhiều giờ, để bạn c&oacute; thể tự tin với đ&ocirc;i mắt tươi s&aacute;ng v&agrave; gương mặt đầy vẻ trẻ trung.&nbsp;</p>\r\n', '<p>Nếu bạn đang t&igrave;m một ph&eacute;p m&agrave;u như vậy,&nbsp;<strong>Maybelline&nbsp;<strong>Instant Age Rewind Treatment Concealer</strong></strong><strong>&nbsp;</strong>ch&iacute;nh l&agrave; sản phẩm d&agrave;nh cho bạn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/Dai/kem-che-khuyet-diem-maybelline-6mlk.jpg\" style=\"height:700px; width:700px\" /></p>\r\n\r\n<p><a href=\"https://hasaki.vn/thuong-hieu/maybelline.html\" target=\"_blank\"><strong>Maybelline</strong>&nbsp;</a>hiện đứng vị tr&iacute; h&agrave;ng đầu tr&ecirc;n thế giới trong d&ograve;ng sản phẩm Mascara với khẩu hiệu: &ldquo;C&oacute; thể c&ocirc; ấy đẹp tự nhi&ecirc;n. C&oacute; thể l&agrave; nhờ Maybelline.&rdquo;&nbsp;<strong>Maybelline</strong>&nbsp;cũng vươn l&ecirc;n l&agrave; thương hiệu trang điểm nổi tiếng tr&ecirc;n thế giới với những si&ecirc;u phẩm d&agrave;nh cho mắt, mặt, m&ocirc;i, được đ&aacute;nh gi&aacute; tốt bởi c&aacute;c t&iacute;n đồ sắc đẹp v&agrave; b&aacute;n chạy ở khắp c&aacute;c nước tr&ecirc;n thế giới. Xuất hiện ch&iacute;nh thức ở Việt Nam v&agrave;o năm 2007, Maybelline tiếp tục ở vị tr&iacute; l&atilde;nh đạo ng&agrave;nh với những đợt tung h&agrave;ng mới với c&ocirc;ng thức vượt trội quấy động v&agrave; khơi nguồn cảm hứng trong cộng đồng l&agrave;m đẹp.</p>\r\n\r\n<p><br />\r\n<img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/nhphuong/PhuongSmall/Maybelline_New_York.jpg\" style=\"height:250px; width:1000px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><strong><strong><a href=\"https://hasaki.vn/danh-muc/che-khuyet-diem-c54.html\" target=\"_blank\">Che Khuyết Điểm</a>&nbsp;<strong><strong><strong>Maybelline&nbsp;</strong></strong></strong>Instant Age Rewind Treatment Concealer&nbsp;</strong></strong></strong>l&agrave; kem che khuyết điểm d&agrave;nh cho v&ugrave;ng dưới mắt với th&agrave;nh phần c&oacute; chứa Haloxyl c&oacute; khả năng che phủ quầng th&acirc;m v&agrave; bọng mắt một c&aacute;ch hiệu quả, đồng thời tạo sức sống cho gương mặt. B&ecirc;n cạnh đ&oacute;, sản phẩm c&ograve;n đồng thời l&agrave;&nbsp;&ldquo;concealer treatment&rdquo;, nếu bạn sử dụng li&ecirc;n tục trong thời gian d&agrave;i, em ấy c&ograve;n khiến cho v&ugrave;ng th&acirc;m &amp; bọng mắt trở n&ecirc;n s&aacute;ng m&agrave;u, c&aacute;c nếp nhăn nhẹ li ti sẽ mờ đi đ&aacute;ng kể.</p>\r\n\r\n<p>Sản phẩm c&oacute;&nbsp;thiết kế dạng b&uacute;t nhỏ xinh v&agrave; th&acirc;n nhựa trong suốt gi&uacute;p bạn dễ d&agrave;ng biết được lượng kem c&ograve;n trong th&acirc;n b&uacute;t. Đầu l&ocirc;ng mềm mượt thiết kế dạng xoay dễ d&agrave;ng kiểm so&aacute;t được lượng kem mỗi lần sử dụng, rất tiện lợi v&agrave; kh&ocirc;ng sợ hao ph&iacute;.&nbsp;<strong><strong><strong><strong><strong><strong>Maybelline&nbsp;</strong></strong></strong>Instant Age Rewind Treatment Concealer</strong></strong></strong>&nbsp;với 6 tone m&agrave;u, trong c&oacute; 2 tone m&agrave;u đặc biệt:&nbsp;Neutralizer 150 (c&oacute; yellow tone cực mạnh, d&agrave;nh cho những người c&oacute; v&ugrave;ng th&acirc;m quầng cực r&otilde;, cần che phủ nhiều nhất) v&agrave; Brightener (c&oacute; khả năng bắt s&aacute;ng cho v&ugrave;ng dưới mắt, tương tự như highlighter).&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/Dai/kem-che-khuyet-diem-maybellinet.jpg\" style=\"height:500px; width:500px\" /></p>\r\n', 1, 6, 10),
(34, 'Kem Chống Nắng La Roche-Posay SPF50+ ', 'KCN01', '399000', 26, '1655723060_CN1.png', '<p><strong>Kem Chống Nắng La Roche-Posay Anthelios Anti-Shine Gel-Cream Dry Touch Finish Mattifying Effect SPF50+&nbsp;</strong>đến từ&nbsp;<strong><a href=\"https://hasaki.vn/thuong-hieu/la-roche-posay.html\" target=\"_blank\">&nbsp;</a><a href=\"https://hasaki.vn/thuong-hieu/la-roche-posay.html\" target=\"_blank\">La Roche-Posay</a></strong>&nbsp;l&agrave; sản phẩm kem chống nắng<strong>&nbsp;</strong>d&agrave;nh cho l&agrave;n da dầu mụn, sở hữu&nbsp;<strong>c&ocirc;ng nghệ cải tiến XL-Protect</strong>&nbsp;c&ugrave;ng&nbsp;<strong>kết cấu kem gel</strong>&nbsp;dịu nhẹ &amp; kh&ocirc;ng nhờn r&iacute;t,&nbsp;gi&uacute;p ngăn ngừa tia UVA/UVB + tia hồng ngoại + t&aacute;c hại từ &ocirc; nhiễm,&nbsp;bảo vệ to&agrave;n diện cho l&agrave;n da lu&ocirc;n khỏe mạnh.</p>\r\n', '<p>&Aacute;nh nắng mặt trời được coi l&agrave; &ldquo;t&aacute;c nh&acirc;n c&oacute; hại&rdquo; số một của l&agrave;n da, g&acirc;y ra hiện tượng n&aacute;m, sạm da. Do đ&oacute;, sử dụng kem chống nắng hằng ng&agrave;y đ&oacute;ng vai tr&ograve; v&ocirc; c&ugrave;ng quan trọng trong việc bảo vệ v&agrave; chăm s&oacute;c l&agrave;n da. Thế nhưng việc lựa chọn kem chống nắng ph&ugrave; hợp cho l&agrave;n da dầu,&nbsp;<a href=\"https://hasaki.vn/danh-muc/mun-c1877.html\" target=\"_blank\">da&nbsp;mụn</a>&nbsp;lại kh&ocirc;ng hề dễ d&agrave;ng. Đa phần c&aacute;c loại kem chống nắng th&ocirc;ng thường đều để lại cảm gi&aacute;c nhờn d&iacute;nh, r&iacute;t da kh&oacute; chịu sau khi thoa, g&acirc;y đọng vệt trắng tr&ecirc;n mặt hoặc kiểm so&aacute;t dầu thừa kh&ocirc;ng tốt, thậm ch&iacute; g&acirc;y b&iacute; tắc lỗ ch&acirc;n l&ocirc;ng v&agrave; l&agrave;m trầm trọng hơn t&igrave;nh trạng mụn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Kem Chống Nắng La Roche-Posay Kiểm Soát Dầu SPF50+ 50ml\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/kem-chong-nang-la-roche-posay-kiem-soat-dau-khong-nhon-rit-50ml-4.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kem Chống Nắng La Roche-Posay Anthelios Anti-Shine Gel-Cream Dry Touch Finish Mattifying Effect SPF50+</strong>&nbsp;được biết đến như một&nbsp;<a href=\"https://hasaki.vn/danh-muc/san-pham-chong-nang-c1871.html\" target=\"_blank\">sản phẩm&nbsp;chống nắng</a>&nbsp;l&yacute; tưởng d&agrave;nh cho da dầu mụn, đặc biệt l&agrave; v&agrave;o m&ugrave;a h&egrave; n&oacute;ng bức. Sản phẩm ứng dụng&nbsp;<strong>c&ocirc;ng nghệ Xl- Protect TM</strong>&nbsp;nhằm chống nắng tối ưu, bảo vệ l&agrave;n da hiệu quả trước tia UVA/UVB cũng như tia hồng ngoại IR v&agrave; t&aacute;c nh&acirc;n &ocirc; nhiễm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Kem Chống Nắng La Roche-Posay Kiểm Soát Dầu SPF50+ 50ml\" src=\"https://media.hasaki.vn/wysiwyg/MinhTu/kem-chong-nang-la-roche-posay-kiem-soat-dau-spf50-50ml_mau-moi-2021_10.jpg\" style=\"height:800px; width:800px\" /></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, hoạt chất Airliciumtm sẽ gi&uacute;p điều h&ograve;a lượng dầu thừa, b&atilde; nhờn &amp; mồ h&ocirc;i vượt trội th&ocirc;ng qua khả năng hấp thụ lượng b&atilde; nhờn gấp 100 lần khối lượng của ch&iacute;nh n&oacute;. Hơn nữa, sản phẩm c&oacute; khả năng chống thấm nước cao, do đ&oacute; bạn c&oacute; thể y&ecirc;n t&acirc;m ngay cả khi bơi lội hay đổ nhiều mồ h&ocirc;i.</p>\r\n\r\n<p>Với kết cấu dạng kem gel độc đ&aacute;o,<strong>&nbsp;La Roche-Posay Anthelios Anti-Shine Gel-Cream Dry Touch Finish Mattifying Effect SPF50+</strong>&nbsp;khi thoa l&ecirc;n da sẽ mang đến cảm gi&aacute;c kh&ocirc; tho&aacute;ng nhanh ch&oacute;ng, thẩm thấu tức th&igrave; v&agrave;o da, ho&agrave;n to&agrave;n kh&ocirc;ng để lại vệt trắng tr&ecirc;n da. C&ugrave;ng th&agrave;nh phần an to&agrave;n l&agrave;nh t&iacute;nh, kh&ocirc;ng g&acirc;y t&igrave;nh trạng k&iacute;ch ứng hay nổi mụn cho l&agrave;n da.&nbsp;</p>\r\n\r\n<p><img alt=\"Kem Chống Nắng La Roche-Posay Kiểm Soát Dầu SPF50+ 50ml\" src=\"https://media.hasaki.vn/wysiwyg/MinhTu/kem-chong-nang-la-roche-posay-kiem-soat-dau-spf50-50ml-5.jpg\" style=\"height:800px; width:800px\" /></p>\r\n\r\n<h2>Loại da ph&ugrave; hợp:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Sản phẩm ph&ugrave; hợp cho da dầu, da mụn, da nhạy cảm.</p>\r\n	</li>\r\n	<li>\r\n	<p>Th&iacute;ch hợp sử dụng cho da mặt v&agrave; cổ.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Giải ph&aacute;p cho t&igrave;nh trạng da:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p><a href=\"https://hasaki.vn/danh-muc/dau-thua-lo-chan-long-to-c1879.html\" target=\"_blank\">Da dầu thừa - lỗ ch&acirc;n l&ocirc;ng to</a>.</p>\r\n	</li>\r\n	<li>\r\n	<p>Da mụn hoặc dễ nổi mụn do b&iacute;t tắc lỗ ch&acirc;n l&ocirc;ng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Kem Chống Nắng La Roche-Posay Kiểm Soát Dầu SPF50+ 50ml\" src=\"https://media.hasaki.vn/wysiwyg/MinhTu/kem-chong-nang-la-roche-posay-kiem-soat-dau-spf50-50ml-1651651092.jpg\" style=\"height:800px; width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>C&ocirc;ng dụng:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Chứa<strong>&nbsp;m&agrave;ng lọc bảo vệ da phổ rộng XL-PROTECT</strong>&nbsp;l&agrave; sự pha trộn giữa c&aacute;c bộ lọc UV v&agrave; hoạt chất chống oxy h&oacute;a mạnh mẽ, kh&ocirc;ng chỉ cung cấp khả năng bảo vệ da khỏi t&aacute;c hại của tia UVA &amp; UVB phổ rộng m&agrave; c&ograve;n chống lại c&aacute;c t&aacute;c nh&acirc;n g&acirc;y hại kh&aacute;c như tia hồng ngoại v&agrave; &ocirc; nhiễm m&ocirc;i trường,&nbsp;ngăn ngừa sạm n&aacute;m, đốm n&acirc;u, t&aacute;c nh&acirc;n g&acirc;y l&atilde;o h&oacute;a sớm &amp;&nbsp;k&iacute;ch ứng &aacute;nh nắng.</p>\r\n	</li>\r\n	<li>\r\n	<p>Chỉ số chống nắng cao<strong>&nbsp;SPF 50+ PA++++</strong>&nbsp;gi&uacute;p bảo vệ da tối ưu dưới &aacute;nh nắng.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kiểm so&aacute;t b&atilde; nhờn hiệu quả với<strong>&nbsp;hoạt chất&nbsp;Airlicium</strong>&nbsp;- chứa c&aacute;c vi hạt si&ecirc;u nhỏ c&oacute; khả năng tan chảy v&agrave; thẩm thấu ngay lập tức v&agrave;o da, hấp thụ b&atilde; nhờn &amp; mồ h&ocirc;i vượt trội gấp 100 lần khối lượng, từ đ&oacute; gi&uacute;p kiểm so&aacute;t mọi nguy&ecirc;n nh&acirc;n dẫn đến t&igrave;nh trạng da b&oacute;ng nhờn (b&atilde; nhờn, mồ h&ocirc;i v&agrave; độ ẩm), duy tr&igrave; l&agrave;n da kh&ocirc; r&aacute;o &amp; tho&aacute;ng mịn.</p>\r\n	</li>\r\n	<li>\r\n	<p>L&agrave;m dịu da v&agrave; hạn chế t&igrave;nh trạng oxy h&oacute;a với th&agrave;nh phần gi&agrave;u&nbsp;<strong>nước kho&aacute;ng La Roche-Posay.</strong></p>\r\n	</li>\r\n	<li>\r\n	<p>Dưỡng ẩm da với<strong>&nbsp;Glycerin,</strong>&nbsp;gi&uacute;p duy tr&igrave; l&agrave;n da ẩm mượt v&agrave; thoải m&aacute;i, kh&ocirc;ng bị kh&ocirc; căng bong tr&oacute;c khi tiếp x&uacute;c với &aacute;nh nắng trong thời gian d&agrave;i.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kết cấu dạng kem gel với&nbsp;<strong>c&ocirc;ng nghệ Dry-touch</strong>&nbsp;thẩm thấu tức th&igrave; v&agrave;o da,&nbsp;mang đến cảm gi&aacute;c kh&ocirc; tho&aacute;ng nhanh ch&oacute;ng, kh&ocirc;ng đọng vệt trắng.</p>\r\n	</li>\r\n	<li>\r\n	<p>C&oacute; thể d&ugrave;ng l&agrave;m lớp l&oacute;t trước khi trang điểm.</p>\r\n	</li>\r\n	<li>C&ocirc;ng thức chống thấm nước th&iacute;ch hợp d&ugrave;ng hằng ng&agrave;y v&agrave; cả những hoạt động ngo&agrave;i trời.</li>\r\n</ul>\r\n\r\n<p><img alt=\"Kem Chống Nắng La Roche-Posay Kiểm Soát Dầu SPF50+ 50ml hasaki\" src=\"https://media.hasaki.vn/wysiwyg/MinhTu/kem-chong-nang-la-roche-posay-kiem-soat-dau-spf50-50ml_mau-moi-2021_1.jpg\" style=\"height:800px; width:800px\" /></p>\r\n\r\n<h2>Độ an to&agrave;n:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Được kiểm nghiệm dưới sự gi&aacute;m s&aacute;t của c&aacute;c chuy&ecirc;n gia da liễu.</p>\r\n	</li>\r\n	<li>\r\n	<p>Không chứa hương liệu, không paraben, không gây bết dính, không để lại vệt trắng, lâu trôi khi sử dụng dưới nước v&agrave; kh&ocirc;ng g&acirc;y mụn trứng c&aacute;.</p>\r\n	</li>\r\n	<li>\r\n	<p>Tuyệt đối an to&agrave;n với da, đặc biệt l&agrave; da nhạy cảm.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Bảo quản:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\r\n	</li>\r\n	<li>\r\n	<p>Để nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đậy nắp k&iacute;n sau khi sử dụng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Th&ocirc;ng số sản phẩm:</h2>\r\n\r\n<p><strong>Dung t&iacute;ch:</strong>&nbsp;15ml &amp; 50ml</p>\r\n\r\n<p><strong>Thương hiệu:</strong>&nbsp;La Roche-Posay</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Ph&aacute;p</p>\r\n\r\n<p><strong>Nơi sản xuất:</strong>&nbsp;Ph&aacute;p</p>\r\n', 1, 4, 9),
(35, 'Kem Chống Nắng La Roche-Posay  50ml ', 'KCN02', '399000', 20, '1655723199_CN2.png', '<p><strong>La Roche-Posay Anthelios Oil Correct Daily Gel-Cream SPF50+&nbsp;</strong>l&agrave; sản phẩm kem chống nắng&nbsp;phổ rộng mới từ thương hiệu La Roche-Posay&nbsp;, với c&ocirc;ng thức chuy&ecirc;n biệt d&agrave;nh cho l&agrave;n da dầu, nhạy cảm v&agrave; mụn, cung cấp khả năng bảo vệ da khỏi tia UV h&agrave;ng ng&agrave;y. Đặc biệt,&nbsp;c&aacute;c hoạt chất da liễu [Salicylic Acid + Niacinamide&nbsp;+ Kẽm + Airlicium] gi&uacute;p l&agrave;m giảm mụn, giảm b&oacute;ng nhờn v&agrave; hỗ trợ se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng.</p>\r\n', '<h2>Loại da ph&ugrave; hợp:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Sản phẩm ph&ugrave; hợp cho da dầu, hỗn hợp thi&ecirc;n dầu, da nhạy cảm, da mụn.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Giải ph&aacute;p cho t&igrave;nh trạng da:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Dầu thường , Lỗ ch&acirc;n l&ocirc;ng to</p>\r\n	</li>\r\n	<li>\r\n	<p>Da nhạy cảm, dễ k&iacute;ch ứng</p>\r\n	</li>\r\n	<li>\r\n	<p>Da mụn</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>C&ocirc;ng dụng nổi bật:</h2>\r\n\r\n<p><strong>Kem Chống Nắng La Roche-Posay Anthelios Oil Correct Daily Gel-Cream SPF50+</strong>&nbsp;chứa phức hợp c&aacute;c hoạt chất da liễu [Salicylic Acid + Niacinamide&nbsp;+ Kẽm + Airlicium] gi&uacute;p mang lại những c&ocirc;ng dụng tuyệt vời cho l&agrave;n da:</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>Khả năng chống nắng rất cao:</strong>&nbsp;nhờ m&agrave;ng lọc chống nắng phổ rộng, gi&uacute;p bảo vệ l&agrave;n da khỏi t&aacute;c hại của tia cực t&iacute;m.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Chống &ocirc; nhiễm:&nbsp;</strong>hạn chế sự b&aacute;m d&iacute;nh của c&aacute;c ph&acirc;n tử &ocirc; nhiễm, bụi mịn l&ecirc;n da.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Chống oxy h&oacute;a:&nbsp;</strong>ngăn ngừa<strong>&nbsp;</strong>stress oxy h&oacute;a g&acirc;y ra bởi &ocirc; nhiễm v&agrave; tia hồng ngoại.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Cải thiện c&aacute;c khuyết điểm:&nbsp;</strong>gi&uacute;p thu nhỏ lỗ ch&acirc;n l&ocirc;ng v&agrave; hỗ trợ sửa chữa c&aacute;c khuyết điểm như&nbsp;<a href=\"https://hasaki.vn/danh-muc/mun-c1877.html\" target=\"_blank\">mụn</a>, th&acirc;m mụn.</p>\r\n	</li>\r\n	<li>\r\n	<p>C&ocirc;ng thức được b&agrave;o chế chuy&ecirc;n biệt d&agrave;nh cho da dầu &amp; nhạy cảm, đồng thời th&iacute;ch hợp cho da mụn.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kết cấu kh&ocirc;ng nhờn, kh&ocirc;ng g&acirc;y b&iacute;t tắc lỗ ch&acirc;n l&ocirc;ng, cho l&agrave;n da được th&ocirc;ng tho&aacute;ng.</p>\r\n	</li>\r\n	<li>\r\n	<p>Khả năng kiểm so&aacute;t dầu thừa l&ecirc;n đến 12 giờ.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Độ an to&agrave;n:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>C&ocirc;ng thức giảm thiểu k&iacute;ch ứng, được thiết kế chuy&ecirc;n biệt cho l&agrave;n da nhạy cảm.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đ&atilde; qua kiểm nghiệm về hiệu quả v&agrave; độ dịu nhẹ tr&ecirc;n l&agrave;n da nhạy cảm.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Kiểm nghiệm l&acirc;m s&agrave;ng:</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Kiểm nghiệm hiệu quả của kem chống nắng La Roche-Posay Anthelios Oil Correct Daily Gel-Cream SPF50+ cho da mụn\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen1/kem-chong-nang-la-roche-posay-cho-da-dau-nhay-cam-50ml-2.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Kiểm nghiệm hiệu quả của kem chống nắng La Roche-Posay Anthelios Oil Correct Daily Gel-Cream SPF50+ trên da bóng nhờn và thâm mụn\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen1/kem-chong-nang-la-roche-posay-cho-da-dau-nhay-cam-50ml-3.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Kem Chống Nắng La Roche-Posay Anthelios Oil Correct Daily Gel-Cream SPF50+ giúp bảo vệ làn da khỏi tác hại của ô nhiễm môi trường\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen1/kem-chong-nang-la-roche-posay-cho-da-dau-nhay-cam-50ml-4.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Bảo quản:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Bảo quản nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp hoặc nơi c&oacute; nhiệt độ cao / ẩm ướt.</p>\r\n	</li>\r\n	<li>\r\n	<p>Tr&aacute;nh xa tầm tay trẻ em.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đậy nắp k&iacute;n sau khi sử dụng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2><strong>Th&ocirc;ng số sản phẩm:</strong></h2>\r\n\r\n<p><strong>Thương hiệu:&nbsp;</strong>La Roche-Posay</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:&nbsp;</strong>Ph&aacute;p</p>\r\n\r\n<p><strong>Sản xuất tại:&nbsp;</strong>Ph&aacute;p</p>\r\n\r\n<p><strong><strong>Dung t&iacute;ch:</strong></strong>&nbsp;50ml</p>\r\n', 1, 4, 9),
(36, 'Kem Nền Maybelline Lâu Trôi 110 ', 'KN01', '179000', 48, '1655723453_kemnen.png', '<p><strong>Kem Nền L&acirc;u Tr&ocirc;i Maybelline SuperStay Long Lasting Full Coverage Foundation 30ml&nbsp;</strong>l&agrave; d&ograve;ng sản phẩm <strong>kem nền</strong>&nbsp;vượt trội mới ra mắt từ <strong>Maybelline Newyork</strong>Với c&ocirc;ng nghệ hạt m&agrave;u gi&uacute;p che phủ mọi khuyết điểm tr&ecirc;n da v&agrave; hạt phấn mỏng, mịn, si&ecirc;u nhẹ, si&ecirc;u l&acirc;u tr&ocirc;i gi&uacute;p lớp nền của bạn ho&agrave;n hảo l&ecirc;n tới 24H m&agrave; kh&ocirc;ng bị chảy hay xuống t&ocirc;ng, kể cả khi gặp nước. Kem nền kiềm dầu, kh&ocirc;ng l&agrave;m b&iacute;t tắc lỗ ch&acirc;n l&ocirc;ng, ph&ugrave; hợp với mọi loại da. Sản phẩm được kiểm nghiệm bởi b&aacute;c sĩ da liễu.</p>\r\n', '<p><strong>Kem Nền Maybelline&nbsp;Che Phủ Cao L&acirc;u Tr&ocirc;i 24H&nbsp;SuperStay Long Lasting Full Coverage Foundation 30ml&nbsp;</strong>hiện đ&atilde; c&oacute; mặt với 4 t&ocirc;ng m&agrave;u cho bạn lựa chọn:</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>T&ocirc;ng da s&aacute;ng:</p>\r\n\r\n	<ul>\r\n		<li>\r\n		<p>110&nbsp;Porcelain</p>\r\n		</li>\r\n		<li>\r\n		<p>112 Natural Ivory</p>\r\n		</li>\r\n		<li>\r\n		<p>120 Classic Ivory</p>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n	<li>\r\n	<p>T&ocirc;ng da trung b&igrave;nh:</p>\r\n\r\n	<ul>\r\n		<li>\r\n		<p>128 Warm Nude</p>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n	<li>\r\n	<p>T&ocirc;ng da tối:</p>\r\n\r\n	<ul>\r\n		<li>\r\n		<p>220 Natural Beige</p>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/kem-nen-maybelline-che-phu-cao-lau-troi-24h-3.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/kem-nen-maybelline-che-phu-cao-lau-troi-24h-4.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Loại da ph&ugrave; hợp:</h2>\r\n\r\n<ul>\r\n	<li>Sản phẩm ph&ugrave; hợp cho mọi loại da, đặc biệt l&agrave; <strong>da dầu</strong></li>\r\n</ul>\r\n\r\n<h2>Giải ph&aacute;p cho t&igrave;nh trạng da:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Da c&oacute; nhiều khuyết điểm như: lỗ ch&acirc;n l&ocirc;ng to, th&acirc;m mụn, da kh&ocirc;ng đều m&agrave;u.</p>\r\n	</li>\r\n	<li>\r\n	<p>Da cần một sản phẩm kem nền c&oacute; độ che phủ cao, kiềm dầu tốt v&agrave; l&acirc;u tr&ocirc;i.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Ưu thế nổi bật:</h2>\r\n\r\n<p><strong>Kem Nền Maybelline SuperStay Long Lasting Full Coverage Foundation&nbsp;</strong>l&yacute; tưởng cho mọi loại da với những ưu điểm nổi bật:</p>\r\n\r\n<ul>\r\n	<li>Lớp nền l&acirc;u tr&ocirc;i l&ecirc;n tới 24H, kh&ocirc;ng lem tr&ocirc;i kể cả khi gặp nước v&agrave; đổ mồ h&ocirc;i, kh&ocirc;ng d&iacute;nh l&ecirc;n trang phục.</li>\r\n	<li>\r\n	<p>Độ che phủ cao, gi&uacute;p che phủ ho&agrave;n hảo c&aacute;c khuyết điểm v&agrave; lỗ ch&acirc;n l&ocirc;ng.</p>\r\n	</li>\r\n	<li>\r\n	<p>Khả năng kiềm dầu tốt, l&yacute; tưởng d&agrave;nh cho da dầu thừa nhiều, cho&nbsp;lớp nền mịn l&igrave; l&acirc;u tr&ocirc;i, hạn chế t&igrave;nh trạng tiết dầu l&agrave;m oxy h&oacute;a, xuống t&ocirc;ng nền.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/kem-nen-maybelline-che-phu-cao-lau-troi-24h.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/kem-nen-maybelline-che-phu-cao-lau-troi-24h-2.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/kem-nen-maybelline-che-phu-cao-lau-troi-24h-5.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Bảo quản:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Bảo quản nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp v&agrave; nhiệt độ cao.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đậy nắp k&iacute;n sau khi sử dụng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Dung t&iacute;ch:</strong>&nbsp;30ml</p>\r\n\r\n<p><strong>Thương hiệu:</strong>&nbsp;Maybelline New York</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Mỹ</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Trung Quốc</p>\r\n\r\n<p><strong>Ng&agrave;y sản xuất:</strong>&nbsp;Xem tr&ecirc;n bao b&igrave; sản phẩm</p>\r\n', 1, 3, 10),
(37, 'Kem Nền Maybelline Mịn Nhẹ Kiềm Dầu  ', 'KN02', '189000', 45, '1655723678_kemnen2.png', '<p><strong>Kem Nền Mịn Nhẹ, Kiềm Dầu Maybelline Fit Me 30ml</strong>&nbsp;l&agrave; d&ograve;ng sản phẩm kem nền đến từ thương hiệu Maybelline&nbsp;của Mỹ,&nbsp;g&acirc;y tiếng vang trong giới l&agrave;m đẹp to&agrave;n thế giới v&agrave; được mệnh danh l&agrave; kem nền &#39;quốc d&acirc;n&#39; khi c&oacute; mặt tại Việt Nam.</p>\r\n', '<p><strong><strong>Kem Nền Fit Me&nbsp;</strong>Matte Poreless Foundation SPF 22&nbsp;</strong>g&acirc;y ấn tượng với khả năng kiềm dầu hiệu quả suốt cả ng&agrave;y d&agrave;i nhờ th&agrave;nh phần đất s&eacute;t kho&aacute;ng, cho lớp nền mịn l&igrave; tự nhi&ecirc;n, đồng thời che phủ tốt mọi khuyết điểm tr&ecirc;n bề mặt da.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Kem Nền Maybelline Mịn Nhẹ Kiềm Dầu Chống Nắng Matte Poreless Foundation SPF 22 30ml\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/kem-nen-maybelline-min-nhe-kiem-dau-chong-nang-30ml-6.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kem Nền Mịn Nhẹ Kiềm Dầu Chống Nắng&nbsp;Maybelline Matte Poreless Foundation SPF 22 30ml</strong>&nbsp;hiện đ&atilde; c&oacute; mặt tại&nbsp;&nbsp;c&oacute; 12 t&ocirc;ng m&agrave;u ph&ugrave; hợp, tiệp mọi t&ocirc;ng da phụ nữ Việt Nam:</p>\r\n\r\n<p><strong>T&Ocirc;NG S&Aacute;NG:</strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>112 Natural Ivory</p>\r\n	</li>\r\n	<li>\r\n	<p>115 Ivory</p>\r\n	</li>\r\n	<li>\r\n	<p>120 Classic Ivory</p>\r\n	</li>\r\n	<li>\r\n	<p>122 Creamy Beige&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>T&Ocirc;NG TRUNG B&Igrave;NH:</strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>125 Nude Beige</p>\r\n	</li>\r\n	<li>\r\n	<p>128 Warm Nude</p>\r\n	</li>\r\n	<li>\r\n	<p>130 Buff Beige</p>\r\n	</li>\r\n	<li>\r\n	<p>220 Natural Beige</p>\r\n	</li>\r\n	<li>\r\n	<p>222 True Beige</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>T&Ocirc;NG NGĂM:</strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>230 Natural Buff</p>\r\n	</li>\r\n	<li>\r\n	<p>235 Pure Beige</p>\r\n	</li>\r\n	<li>\r\n	<p>310 Sun Beige</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Kem Nền Maybelline Mịn Nhẹ Kiềm Dầu Chống Nắng Matte Poreless Foundation SPF 22 \" src=\"https://media.hasaki.vn/wysiwyg/minhchau/kem-nen-maybelline-min-nhe-kiem-dau-chong-nang-30ml-2.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\" Maybelline Matte Poreless Foundation SPF 22 30ml\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/kem-nen-maybelline-min-nhe-kiem-dau-chong-nang-30ml-3.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Loại da ph&ugrave; hợp:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Da thường</p>\r\n	</li>\r\n	<li>\r\n	<p>Da hỗn hợp</p>\r\n	</li>\r\n	<li>\r\n	<p>Da dầu</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Giải ph&aacute;p t&igrave;nh trạng da:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Da thường xuy&ecirc;n tiết nhờn, đổ dầu.</p>\r\n	</li>\r\n	<li>\r\n	<p>Da c&oacute; khuyết điểm.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Ưu thế nổi bật:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Khả năng kiềm dầu tốt, cho lớp nền lu&ocirc;n mịn l&igrave;.</p>\r\n	</li>\r\n	<li>\r\n	<p>Độ che phủ trung b&igrave;nh - cao, che phủ khuyết điểm v&agrave; lỗ ch&acirc;n l&ocirc;ng tốt.</p>\r\n	</li>\r\n	<li>\r\n	<p>Hiệu ứng finish mịn nhẹ, tự nhi&ecirc;n, tho&aacute;ng da.</p>\r\n	</li>\r\n	<li>\r\n	<p>Bảng m&agrave;u với 12 t&ocirc;ng, ph&ugrave; hợp cho mọi t&ocirc;ng da kh&aacute;c nhau.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kem nền l&yacute; tưởng cho da thường, da hỗn hợp v&agrave; da dầu.</p>\r\n	</li>\r\n	<li>\r\n	<p>Bao b&igrave; cải tiến với đầu nhấn tiện lợi.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Maybelline Matte Poreless Foundation SPF 22 \" src=\"https://media.hasaki.vn/wysiwyg/minhchau/kem-nen-maybelline-min-nhe-kiem-dau-chong-nang-30ml-1.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Kem Nền Maybelline Matte Poreless Foundation SPF 22 bảng màu\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/kem-nen-maybelline-min-nhe-kiem-dau-chong-nang-30ml-5.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Kem Nền Maybelline Mịn Nhẹ Kiềm Dầu 30ml bảng màu \" src=\"https://media.hasaki.vn/wysiwyg/minhchau/kem-nen-maybelline-min-nhe-kiem-dau-chong-nang-30ml-4.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Bảo quản:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Nơi kh&ocirc; r&aacute;o v&agrave; tho&aacute;ng m&aacute;t.</p>\r\n	</li>\r\n	<li>\r\n	<p>Tr&aacute;nh &aacute;nh nắng trực tiếp, nơi c&oacute; nhiệt độ cao hoặc ẩm ướt.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đậy nắp k&iacute;n sau khi sử dụng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Th&ocirc;ng số sản phẩm:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>Dung t&iacute;ch:</strong>&nbsp;30ml</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Thương hiệu:</strong>&nbsp;Maybelline</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Mỹ</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Sản xuất tại:</strong>&nbsp;Trung Quốc</p>\r\n	</li>\r\n</ul>\r\n', 1, 3, 10);
INSERT INTO `tbl_sanpham` (`id_sanpham`, `tensanpham`, `masp`, `giasp`, `soluong`, `hinhanh`, `tomtat`, `noidung`, `tinhtrang`, `id_danhmuc`, `id_hieusp`) VALUES
(38, 'Kem Chống Nắng SomeByMi', 'KCN03', '219000', 39, '1655723866_CN3.png', '<p><strong>Kem Chống Nắng SOME BY MI Truecica Mineral Calming Suncream SPF50+/PA++++ 50ml&nbsp;</strong>đến từ thương hiệu SOM BY ME<strong>&nbsp;</strong>được thiết kế d&agrave;nh ri&ecirc;ng cho l&agrave;n da nhạy cảm v&agrave; dễ bị mụn, mang đến c&ocirc;ng dụng 3 trong 1: chống nắng, dưỡng s&aacute;ng da, giảm nếp nhăn.</p>\r\n', '<p>&Aacute;nh nắng mặt trời l&agrave; một trong những nguy&ecirc;n nh&acirc;n h&agrave;ng đầu dẫn đến l&atilde;o h&oacute;a da sớm, b&ecirc;n cạnh qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a tự nhi&ecirc;n do vấn đề tuổi t&aacute;c. Việc tiếp x&uacute;c thường xuy&ecirc;n qu&aacute; mức với &aacute;nh nắng mặt trời sẽ khiến da mất đi Collagen v&agrave; Elastin, đồng thời l&agrave;m suy yếu đi qu&aacute; tr&igrave;nh tổng hợp Collagen mới, dẫn đến da mất đi độ đ&agrave;n hồi v&agrave; săn chắc vốn c&oacute;, trở n&ecirc;n ch&ugrave;ng nh&atilde;o, chảy xệ, h&igrave;nh th&agrave;nh nếp nhăn&hellip; Ngo&agrave;i ra &aacute;nh nắng mặt trời c&ograve;n khiến da trở n&ecirc;n đen sạm, h&igrave;nh th&agrave;nh c&aacute;c đốm sắc tố như t&agrave;n nhang, th&acirc;m n&aacute;m, đồi mồi&hellip; V&igrave; thế, việc sử dụng <strong>KEM CHỐNG NẮNG</strong>&nbsp;hằng ng&agrave;y l&agrave; cực k&igrave; quan trọng v&agrave; l&agrave; bước đầu ti&ecirc;n gi&uacute;p ngăn ngừa l&atilde;o h&oacute;a da sớm m&agrave; bạn tuyệt đối kh&ocirc;ng thể bỏ qua!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Bao bì Kem Chống Nắng SOME BY MI Truecica Mineral Calming Suncream SPF50+/PA++++ 50ml \" src=\"https://media.hasaki.vn/wysiwyg/minhchau/kem-chong-nang-some-by-mi-spf50-pa-50ml-2.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>SOME BY MI&nbsp;</strong>l&agrave; thương hiệu đến từ H&agrave;n Quốc, chuy&ecirc;n sản xuất c&aacute;c sản phẩm l&agrave;m đẹp thiết thực nhưng vẫn hợp thời trang. C&aacute;c sản phẩm của h&atilde;ng được chiết xuất từ c&aacute;c th&agrave;nh phần thi&ecirc;n nhi&ecirc;n, kể cả c&aacute;c sản phẩm chăm s&oacute;c da đến c&aacute;c sản phẩm trang điểm. Nổi tiếng nhất phải kể đến l&agrave;&nbsp;<strong>Nước Hoa Hồng Cho Da Mụn&nbsp;</strong><strong>Miracle Toner AHA- BHA-PHA 30 Days&nbsp;</strong>đ&atilde; nhận được &quot;cơn mưa lời khen&quot; từ cộng đồng l&agrave;m đẹp nhờ khả năng l&agrave;m giảm mụn v&agrave; mang lại l&agrave;n da mịn m&agrave;ng, khỏe mạnh chỉ trong 30 ng&agrave;y sử dụng đều đặn. Tiếp nối th&agrave;nh c&ocirc;ng,&nbsp;<strong>Some By Mi</strong>&nbsp;tiếp tục cho ra đời&nbsp;<strong>Kem Chống Nắng SOME BY MI Truecica Mineral Calming Suncream SPF50+/PA++++&nbsp;</strong>để ho&agrave;n thiện <strong>quy tr&igrave;nh chăm s&oacute;c da</strong>&nbsp;mụn, nhạy cảm.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Kết cấu Kem Chống Nắng SOME BY MI Truecica Mineral Calming Suncream SPF50+/PA++++ 50ml \" src=\"https://media.hasaki.vn/wysiwyg/minhchau/kem-chong-nang-some-by-mi-spf50-pa-50ml-5.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Truecica Mineral Calming Suncream&nbsp;</strong>c&oacute; chỉ số SPF50+ PA++++ gi&uacute;p bảo vệ da to&agrave;n diện trước tia UVA/UVB g&acirc;y hại, ngăn ngừa c&aacute;c t&aacute;c nh&acirc;n g&acirc;y l&atilde;o h&oacute;a sớm, dưỡng da s&aacute;ng hồng mịn m&agrave;ng. Sản phẩm sử dụng 100% m&agrave;ng lọc chống nắng vật l&yacute; kh&ocirc;ng chứa th&agrave;nh phần hạt nano gi&uacute;p chống nắng hiệu quả v&agrave; bền vững suốt cả ng&agrave;y d&agrave;i m&agrave; vẫn an to&agrave;n cho da, kh&ocirc;ng g&acirc;y k&iacute;ch ứng.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, sản phẩm chứa đến 85% tinh chất dưỡng ẩm cho da, nu&ocirc;i dưỡng cho l&agrave;n da mềm mượt, kh&ocirc;ng bị kh&ocirc; căng hay bong tr&oacute;c trong suốt qu&aacute; tr&igrave;nh sử dụng. Đặc biệt, kết cấu dạng kem-sữa lỏng nhẹ (lotion) thẩm thấu nhanh ch&oacute;ng v&agrave;o da v&agrave; kh&ocirc;ng hề để lại lớp m&agrave;ng trắng bệt như những sản phẩm kem chống nắng thuần vật l&yacute; th&ocirc;ng thường. Ph&ugrave; hợp cho mọi loại da, mọi t&ocirc;ng da.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Kết cấu Kem Chống Nắng SOME BY MI Truecica Mineral Calming Suncream SPF50+/PA++++ \" src=\"https://media.hasaki.vn/wysiwyg/minhchau/kem-chong-nang-some-by-mi-spf50-pa-50ml-3.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;c th&agrave;nh phần dưỡng chất nu&ocirc;i dưỡng l&agrave;n da hiệu quả bao gồm:</p>\r\n\r\n<ul>\r\n	<li>Chiết xuất từ ngải cứu: cấp nước, c&acirc;n bằng &amp; l&agrave;m dịu da.</li>\r\n	<li>Chiết xuất từ tr&agrave;m tr&agrave;: gi&uacute;p bảo vệ, l&agrave;m dịu l&agrave;n da nhạy cảm, dễ k&iacute;ch ứng.</li>\r\n	<li>Chiết xuất từ rau m&aacute;: gi&uacute;p bảo vệ l&agrave;n da trước c&aacute;c t&aacute;c nh&acirc;n g&acirc;y hại từ b&ecirc;n ngo&agrave;i, l&agrave;m dịu da, hỗ trợ phục hồi da, l&agrave;m mờ sẹo mụn&hellip;</li>\r\n	<li>Centella powder extract: gi&uacute;p tăng cường h&agrave;ng r&agrave;o bảo vệ da v&agrave; l&agrave;m dịu da hiệu quả.</li>\r\n	<li>Phức hợp độc quyền Truecica&trade; chứa 10.000 PPM chiết xuất từ l&aacute; c&acirc;y tr&agrave;m tr&agrave;: gi&uacute;p l&agrave;m dịu da, giảm mụn v&agrave; nu&ocirc;i dưỡng da khỏe mạnh.</li>\r\n	<li>Niacinamide, Adenosine: gi&uacute;p dưỡng s&aacute;ng, giảm sưng vi&ecirc;m, ngăn ngừa l&atilde;o h&oacute;a cho da.</li>\r\n</ul>\r\n\r\n<p><strong>Truecica Mineral Calming Suncream&nbsp;</strong>kh&ocirc;ng chứa m&agrave;u nh&acirc;n tạo, hương liệu v&agrave; 20 th&agrave;nh phần c&oacute; khả năng g&acirc;y hại cho da kh&aacute;c.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Độ an toàn Kem Chống Nắng SOME BY MI Truecica Mineral Calming Suncream SPF50+/PA++++ 50ml \" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/kem-chong-nang-some-by-mi-spf50-pa-50ml-6.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Loại da ph&ugrave; hợp:</strong></p>\r\n\r\n<p>Sản phẩm ph&ugrave; hợp cho mọi loại da, đặc biệt l&agrave; da nhạy cảm, dễ bị mụn.</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:</strong></p>\r\n\r\n<ul>\r\n	<li>SPF50+ PA++++ bảo vệ da trước tia UVA/UVB g&acirc;y hại.</li>\r\n	<li>100% m&agrave;ng lọc chống nắng vật l&yacute;, an to&agrave;n v&agrave; bền vững tr&ecirc;n da.</li>\r\n	<li>85% tinh chất dưỡng ẩm giữ da lu&ocirc;n mềm mượt.</li>\r\n	<li>Chứa 10.000ppm hợp chất Truecica&trade; gi&uacute;p l&agrave;m dịu, bảo vệ v&agrave; củng cố h&agrave;ng r&agrave;o bảo vệ da.</li>\r\n	<li>Kết cấu lỏng nhẹ, thấm nhanh, kh&ocirc;ng trắng mặt.</li>\r\n	<li>Kh&ocirc;ng chứa c&aacute;c th&agrave;nh phần g&acirc;y hại, an to&agrave;n cho mọi loại da.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Kem Chống Nắng SOME BY MI SPF50+/PA++++ 50ml \" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/kem-chong-nang-some-by-mi-spf50-pa-50ml-7.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bảo quản:</strong></p>\r\n\r\n<p>Đậy nắp lại sau khi sử dụng.</p>\r\n\r\n<p>Bảo quản tr&aacute;nh xa tầm tay trẻ nhỏ.</p>\r\n\r\n<p>Kh&ocirc;ng đặt ở những nơi c&oacute; &aacute;nh s&aacute;ng trực tiếp chiếu v&agrave;o, hay những nơi c&oacute; nhiệt độ qu&aacute; cao hoặc qu&aacute; thấp.</p>\r\n\r\n<p><strong>Dung t&iacute;ch:</strong>&nbsp;50ml</p>\r\n\r\n<p><strong>Thương hiệu:</strong>&nbsp;SOME BY MI</p>\r\n\r\n<p><strong>Xuất xứ:</strong>&nbsp;H&agrave;n Quốc</p>\r\n', 1, 4, 7),
(39, 'Kem Nền Lemonade Màu 02', 'KN03', '379000', 28, '1655724593_kn3.png', '<p><strong>Kem Nền Lemonade Perfect Couple Dual Foundation 30g</strong>&nbsp;l&agrave; sản phẩm t&iacute;ch hợp 2 bước kem l&oacute;t&nbsp;v&agrave; kem nền&nbsp;trong một đến từ thương hiệu Lemonade của Việt Nam. Sản phẩm tạo n&ecirc;n lớp nền mỏng nhẹ, căng b&oacute;ng tự nhi&ecirc;n, với độ che phủ tối ưu, bền m&agrave;u suốt cả ng&agrave;y đồng thời kiểm so&aacute;t dầu thừa v&agrave; giữ lại độ ẩm tự nhi&ecirc;n của da.&nbsp;<strong>Perfect Couple Dual Foundation</strong>&nbsp;c&oacute; 3 t&ocirc;ng m&agrave;u ph&ugrave; hợp cho đa dạng m&agrave;u&nbsp;da của người Việt Nam, từ da ngăm đến da trắng s&aacute;ng.</p>\r\n', '<p>Hiện sản phẩm&nbsp;<strong>Kem Nền Lemonade Perfect Couple Dual Foundation 30g</strong>&nbsp;đ&atilde; c&oacute; mặt tại với 3 m&agrave;u:</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>01 Light</strong>&nbsp;(S&aacute;ng hơn m&agrave;u A01 Light của Cushion một ch&uacute;t)</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>02 Medium Light</strong>&nbsp;(Bằng m&agrave;u A02 Natural của Cushion)</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>03 Medium</strong>&nbsp;(Tối hơn m&agrave;u A02 Natural của Cushion một ch&uacute;t)</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Bảng màu Kem Nền Lemonade Perfect Couple Dual Foundation 30g\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/kem-nen-lemonade-ket-hop-2-trong-1-30g-2.jpg\" style=\"height:800px; width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Loại da ph&ugrave; hợp:</h3>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Sản phẩm c&oacute; thể điều tiết độ ẩm v&agrave; b&atilde; nhờn v&igrave; vậy ph&ugrave; hợp với mọi loại da.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h3>Giải ph&aacute;p t&igrave;nh trạng da:</h3>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Da c&oacute; khuyết điểm như vết th&acirc;m mụn, mụn đỏ,&hellip;.</p>\r\n	</li>\r\n	<li>\r\n	<p>Da kh&ocirc;ng đều m&agrave;u, xỉn m&agrave;u.</p>\r\n	</li>\r\n	<li>\r\n	<p>Da thường đổ dầu hay kh&ocirc; bong tr&oacute;c.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h3>Ưu thế nổi bật:</h3>\r\n\r\n<p><strong><em>Kem l&oacute;t:</em></strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Chứa c&ocirc;ng nghệ ngậm nước gi&uacute;p da duy tr&igrave; độ ẩm v&agrave; kiểm so&aacute;t dầu thừa.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kết cấu dạng gel trong suốt mỏng nhẹ, thẩm thấu nhanh c&oacute; thể d&ugrave;ng với bất kỳ kem nền hay phấn nước kh&aacute;c.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong><em>Kem nền:</em></strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>C&ocirc;ng nghệ&nbsp;<strong>Super Fit Polymer</strong>&nbsp;c&ugrave;ng hạt phấn si&ecirc;u nhỏ gi&uacute;p kiểm so&aacute;t mồ hồi, dầu thừa, b&atilde; nhờn cho lớp nền như &yacute; suốt cả ng&agrave;y.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kem nền c&oacute; độ che phủ cao, bền m&agrave;u l&acirc;u tr&ocirc;i suốt 18 giờ.</p>\r\n	</li>\r\n	<li>\r\n	<p>C&oacute; chỉ số chống nắng&nbsp;<strong>SPF 30+ v&agrave; PA+++</strong>&nbsp;v&agrave; c&aacute;c dưỡng chất hỗ trợ bảo bệ da dưới &aacute;nh nắng v&agrave; ngăn ngừa l&atilde;o ho&aacute; da.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kết cấu lỏng si&ecirc;u nhẹ, dễ d&agrave;ng thao t&aacute;c t&aacute;n kh&ocirc;ng g&acirc;y nặng mặt.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Lemonade Perfect Couple Dual Foundation 30g\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/kem-nen-lemonade-ket-hop-2-trong-1-30g-3.jpg\" style=\"height:800px; width:800px\" /></p>\r\n', 1, 3, 13),
(40, 'Phấn Nước Lemonade Siêu Kiềm Dầu', 'KN04', '279000', 40, '1655724734_kn04.png', '<p><strong>Phấn Nước Lemonade Si&ecirc;u Kiềm Dầu 15g</strong><strong>&nbsp;</strong>l&agrave; sản phẩm Cushion&nbsp;đến từ Lemonade&nbsp;của Việt Nam, được thiết kế cho l&agrave;n da dầu, si&ecirc;u dầu v&agrave; da dầu mụn nhờ c&oacute; lớp m&agrave;ng cực kỳ bền vững, kiểm so&aacute;t dầu thừa hữu hiệu, hạn chế vỡ hay chảy nền do mồ h&ocirc;i v&agrave; b&atilde; nhờn dư thừa. Từ đ&oacute;, sản phẩm gi&uacute;p giữ lớp nền bền chắc, &iacute;t xuống m&agrave;u v&agrave; l&acirc;u tr&ocirc;i.</p>\r\n', '<p>Hiện sản phẩm&nbsp;<strong>Phấn Nước Lemonade Supermatte Cushion</strong><strong>&nbsp;</strong><strong>15g</strong><strong>&nbsp;</strong>đ&atilde; c&oacute; mặt<strong>&nbsp;</strong>với c&aacute;c m&agrave;u:</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>01 Light:</strong>&nbsp;ph&ugrave; hợp cho tone da trắng s&aacute;ng nhưng kh&ocirc;ng phải trắng bệt v&agrave; nhợt nhạt.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>02 Medium Light:&nbsp;</strong>ph&ugrave; hợp cho tone da trung b&igrave;nh đến trung b&igrave;nh s&aacute;ng, tự nhi&ecirc;n v&agrave; h&agrave;i h&ograve;a, đ&acirc;y cũng l&agrave; m&agrave;u sắc dễ ph&ugrave; hợp với đại đa số m&agrave;u da.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>03 Medium:&nbsp;</strong>ph&ugrave; hợp cho tone da tự nhi&ecirc;n, da v&agrave;ng hoặc da hơi tối m&agrave;u.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Bảng màu Phấn Nước Lemonade Siêu Kiềm Dầu 15g\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/phan-nuoc-lemonade-sieu-kiem-dau-mau-03-medium-15g-4.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>Loại da ph&ugrave; hợp:</strong></h3>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Da dầu mụn</p>\r\n	</li>\r\n	<li>\r\n	<p>Da dầu hoặc da si&ecirc;u dầu</p>\r\n	</li>\r\n	<li>\r\n	<p>Da hỗn hợp dầu</p>\r\n	</li>\r\n</ul>\r\n\r\n<h3><strong>Giải ph&aacute;p cho t&igrave;nh trạng da:</strong></h3>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Da thường xuy&ecirc;n đổ dầu thừa.</p>\r\n	</li>\r\n	<li>\r\n	<p>Da tiết dầu khiến lớp nền dễ bị xuống t&ocirc;ng, vỡ nền.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><strong><img alt=\"Thiết kế Phấn Nước Lemonade Siêu Kiềm Dầu 15g\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/phan-nuoc-lemonade-sieu-kiem-dau-mau-03-medium-15g-2.jpg\" style=\"width:800px\" /></strong></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><img alt=\"Thành phần Phấn Nước Lemonade Siêu Kiềm Dầu 15g\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/phan-nuoc-lemonade-sieu-kiem-dau-mau-03-medium-15g-3.jpg\" style=\"width:800px\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><img alt=\"Công dụng Phấn Nước Lemonade Siêu Kiềm Dầu 15g\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/phan-nuoc-lemonade-sieu-kiem-dau-mau-03-medium-15g-7.jpg\" style=\"width:800px\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>Ưu thế nổi bật:</strong></h3>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Kết cấu hạt phấn mềm mại si&ecirc;u nhẹ với độ kết d&iacute;nh cao gi&uacute;p lớp nền b&aacute;m v&agrave; bền chặt, tạo lớp trang điểm cực kỳ mượt m&agrave;, tự nhi&ecirc;n v&agrave; nhẹ mặt.</p>\r\n	</li>\r\n	<li>\r\n	<p>Bổ sung dầu dưỡng si&ecirc;u nhẹ dễ bay hơi v&agrave;o dung m&ocirc;i tạo bề mặt để gi&uacute;p lớp nền dễ d&agrave;ng tiệp nhanh v&agrave;o da sau mỗi lần chạm, đồng thời dầu sẽ bay hơi ngay sau khi thoa để cho ra lớp nền lỳ v&agrave; r&aacute;o mịn.</p>\r\n	</li>\r\n	<li>\r\n	<p>Hạt phấn h&uacute;t dầu si&ecirc;u nhỏ gi&uacute;p h&uacute;t hết dầu thừa v&agrave; kiểm so&aacute;t dầu nhờn đến 10 tiếng đồng hồ, độ b&aacute;m nền l&ecirc;n đến 24 giờ.</p>\r\n	</li>\r\n	<li>\r\n	<p>Chứa&nbsp;<strong>2% Niacinamide</strong>&nbsp;gi&uacute;p l&agrave;m dịu da, giảm sưng tấy mụn, kh&aacute;ng vi&ecirc;m kh&aacute;ng khuẩn cho da, hạn chế ph&aacute;t sinh mụn v&agrave; hỗ trợ điều tiết độ ẩm tr&ecirc;n da.</p>\r\n	</li>\r\n	<li>\r\n	<p>Th&agrave;nh phần chống nắng cao nằm ở vị tr&iacute; thứ 4 của bảng th&agrave;nh phần với chỉ số l&ecirc;n đến&nbsp;<strong>SPF50+ PA+++</strong>&nbsp;để tăng cường bảo vệ da cả ng&agrave;y.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><img alt=\"Phân biệt các loại cushion Lemonade\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/phan-nuoc-lemonade-sieu-kiem-dau-mau-03-medium-15g-6.jpg\" style=\"width:800px\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><img alt=\"Phấn Nước Lemonade Supermatte Cushion 15g\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/phan-nuoc-lemonade-sieu-kiem-dau-mau-03-medium-15g-5.jpg\" style=\"width:800px\" /></strong></p>\r\n', 1, 3, 13),
(41, 'Son Kem Lemonade Đỏ Cam Nâu', 'son01', '189000', 56, '1655724922_CON1.png', '<p><strong>Son Kem L&igrave; Lemonade&nbsp;SuperNatural Matte Lipcream 5g</strong>&nbsp;l&agrave; d&ograve;ng son kem l&igrave; mới nhất vừa được ra mắt từ&nbsp;<strong><a href=\"https://hasaki.vn/thuong-hieu/lemonade.html\" target=\"_blank\">thương hiệu mỹ phẩm trang điểm&nbsp;LEMONADE </a>,&nbsp;</strong>được lấy cảm hứng&nbsp;từ những si&ecirc;u năng lực tiềm ẩn của mọi c&ocirc; g&aacute;i, bao gồm&nbsp;5&nbsp;thỏi son kem l&igrave; với t&ocirc;ng m&agrave;u &quot;cực T&acirc;y&quot; c&aacute; t&iacute;nh, ph&aacute; c&aacute;ch v&agrave; kh&aacute;c biệt ho&agrave;n to&agrave;n so với những thỏi son trước đ&acirc;y bạn từng thấy, tuy nhi&ecirc;n son vẫn đảm bảo được sự tiện dụng v&agrave; ph&ugrave; hợp cho mọi m&agrave;u da, độ tuổi.</p>\r\n', '<p><strong>LEMONADE SUPERNATURAL LIPCREAM</strong></p>\r\n\r\n<p><strong>KHI ĐẸP L&Agrave; MỘT SI&Ecirc;U NĂNG LỰC</strong></p>\r\n\r\n<p>Ai ai cũng c&oacute; một &ldquo;si&ecirc;u năng lực&rdquo; ri&ecirc;ng của bản th&acirc;n m&igrave;nh, tuy nhi&ecirc;n kh&ocirc;ng phải bất kỳ c&ocirc; g&aacute;i n&agrave;o cũng kh&aacute;m ph&aacute; ra được. Super Natural - năng lực Si&ecirc;u Nhi&ecirc;n gi&uacute;p c&aacute;c n&agrave;ng bức ph&aacute; giới hạn của bản th&acirc;n, mang đến vẻ đẹp c&aacute; t&iacute;nh, vẻ đẹp ri&ecirc;ng biệt của mỗi c&ocirc; n&agrave;ng. ĐẸP ch&iacute;nh l&agrave; một si&ecirc;u năng lực đặc biệt nhất, gi&uacute;p mọi c&ocirc; g&aacute;i trở n&ecirc;n tự tin, mạnh mẽ hơn bất kỳ nguồn năng lượng n&agrave;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-kem-li-lemonade-supernatural-5g-1.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>5 m&agrave;u&nbsp;<strong>Son Kem L&igrave; Lemonade&nbsp;SuperNatural Matte Lipcream</strong>&nbsp;được đặt t&ecirc;n theo những &quot;si&ecirc;u năng lực&quot; m&agrave; mọi c&ocirc; g&aacute;i đều khao kh&aacute;t c&oacute; được:</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>01 Mind Control:&nbsp;</strong>l&agrave; một m&agrave;u N&acirc;u Cam cực T&acirc;y nhưng lại kh&ocirc;ng bợt da, sắc n&acirc;u pha cam n&agrave;y sẽ mang đến vẻ đẹp c&oacute; thể kiểm so&aacute;t được t&acirc;m tr&iacute; những người đối diện, thu h&uacute;t &aacute;nh nh&igrave;n v&agrave; nổi bật giữa đ&aacute;m đ&ocirc;ng.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>02 Forever Young:&nbsp;</strong>l&agrave; sắc Cam Đất trẻ trung c&oacute; pha một ch&uacute;t sắc hồng h&agrave;i h&ograve;a v&agrave; tươi tắn hơn, rất th&iacute;ch hợp d&ugrave;ng để đi học đi chơi hằng ng&agrave;y ng&agrave;y m&agrave; kh&ocirc;ng cần trang điểm cầu kỳ.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>03 Angel Wings:&nbsp;</strong>m&agrave;u son Đỏ Cam N&acirc;u ph&ugrave; hợp mọi m&agrave;u da, Angel Wings được pha trộn tỉ lệ v&agrave;ng giữa sắc đỏ-cam v&agrave; n&acirc;u sẽ gi&uacute;p mọi c&ocirc; n&agrave;ng đều tr&ocirc;ng c&aacute; t&iacute;nh hơn, đặc biệt c&oacute; sự tham gia của sắc n&acirc;u n&ecirc;n bạn y&ecirc;n t&acirc;m sẽ kh&ocirc;ng bị xỉn men răng như c&aacute;c m&agrave;u đỏ cam th&ocirc;ng thường.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>04 Super Strong:</strong>&nbsp;Cam Đỏ Đất<strong>&nbsp;</strong>l&agrave; m&agrave;u son đặc biệt nhất bảng m&agrave;u, khi thoa một lớp mỏng sẽ cho bạn một m&agrave;u MLBB (My Lips But Better) n&acirc;u n&acirc;u cam cam cực xinh, nhưng khi chồng nhiều lớp sẽ cho bạn một đ&ocirc;i m&ocirc;i c&aacute; t&iacute;nh, mạnh mẽ.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>05 Hot As Fire:</strong>&nbsp;Đỏ Đất<strong>&nbsp;</strong>l&agrave; m&agrave;u son kh&ocirc;ng thể kh&ocirc;ng c&oacute; trong bộ sưu tập son của mọi c&ocirc; g&aacute;i, s&aacute;ng da - trắng răng - ph&ugrave; hợp mọi độ tuổi từ gi&agrave; trẻ lớn b&eacute;. Đặc biệt, đ&uacute;ng với t&ecirc;n gọi Hot As Fire, bạn sẽ l&agrave; c&ocirc; n&agrave;ng &quot;n&oacute;ng bỏng&quot; v&agrave; tỏa s&aacute;ng khi sử dụng m&agrave;u son n&agrave;y.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-kem-li-lemonade-supernatural-5g-5.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Son Kem L&igrave; Lemonade&nbsp;SuperNatural Matte Lipcream 5g</strong>&nbsp;hiện đ&atilde; c&oacute; mặt tại&nbsp;<strong>Hasaki</strong>.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-kem-li-lemonade-supernatural-5g-2.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-kem-li-lemonade-supernatural-5g-3.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Ưu điểm nổi bật:</strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Bảng m&agrave;u bao gồm 5 tone n&acirc;u T&acirc;y c&aacute; t&iacute;nh, ph&aacute; c&aacute;ch, gi&uacute;p c&aacute;c n&agrave;ng thỏa sức thể hiện &quot;si&ecirc;u năng lực&quot; của bản th&acirc;n m&igrave;nh.</p>\r\n	</li>\r\n	<li>\r\n	<p>Chất son liquid chuẩn l&igrave;, bền m&agrave;u nhưng cực kỳ nhẹ m&ocirc;i.</p>\r\n	</li>\r\n	<li>\r\n	<p>&Aacute;p dụng c&ocirc;ng nghệ sản xuất hiện đại, ti&ecirc;n tiến nhất đến từ H&agrave;n Quốc gi&uacute;p m&ocirc;i lu&ocirc;n được dưỡng mềm mại, mỏng nhẹ, kh&ocirc;ng hề g&acirc;y v&oacute;n cục hay bết d&iacute;nh tr&ecirc;n m&ocirc;i.&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p>C&ocirc;ng nghệ water-proof gi&uacute;p son bền m&agrave;u v&agrave; l&acirc;u tr&ocirc;i khi tiếp x&uacute;c với nước, gi&uacute;p bạn thoải m&aacute;i ăn uống m&agrave; kh&ocirc;ng lo son bị tr&ocirc;i đi nhanh ch&oacute;ng.</p>\r\n	</li>\r\n	<li>\r\n	<p>Thiết kế thỏi son trụ với m&agrave;u hồng gold chủ đạo, th&acirc;n son trong suốt gi&uacute;p bạn dễ d&agrave;ng thấy được m&agrave;u son b&ecirc;n trong m&agrave; kh&ocirc;ng cần phải mở nắp.</p>\r\n	</li>\r\n	<li>\r\n	<p>Cọ m&ocirc;i nhỏ gọn gi&uacute;p dễ d&agrave;ng viền m&ocirc;i.&nbsp;Vỏ hộp giấy xinh xắn với 2 m&agrave;u hồng - đen sang trọng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-kem-li-lemonade-supernatural-5g-4.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Bảo quản:</strong></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t.</p>\r\n	</li>\r\n	<li>\r\n	<p>Tr&aacute;nh &aacute;nh nắng trực tiếp, nơi c&oacute; nhiệt độ cao hoặc ẩm ướt.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đậy nắp k&iacute;n sau khi sử dụng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Dung t&iacute;ch:&nbsp;</strong>5g</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:&nbsp;</strong>Việt Nam</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;H&agrave;n Quốc</p>\r\n\r\n<p><strong>Thương hiệu:&nbsp;</strong>LEMONADE</p>\r\n', 1, 2, 13),
(42, 'Son LEMONADE Màu Cam Nâu ', 'son02', '259000', 42, '1655725141_son3.png', '<p>Với con g&aacute;i, son m&ocirc;i chưa bao giờ l&agrave; đủ. C&aacute;c n&agrave;ng lu&ocirc;n muốn sở hữu thật nhiều t&ocirc;ng m&agrave;u son kh&aacute;c nhau để c&oacute; thể biến ho&aacute; make-up look của m&igrave;nh thật đa dạng, từ c&aacute; t&iacute;nh, quyến rũ hay ngọt ng&agrave;o, dễ thương chỉ trong chớp mắt. Ch&iacute;nh v&igrave; vậy m&agrave; th&uacute; vui của chị em ch&uacute;ng m&igrave;nh l&agrave; săn l&ugrave;ng những mẫu son mới để y&ecirc;u chiều đ&ocirc;i m&ocirc;i xinh thay đổi mỗi ng&agrave;y.&nbsp;</p>\r\n', '<p>Thấu hiểu điều n&agrave;y, n&agrave;ng &ldquo;ph&ugrave; thuỷ makeup&rdquo; Qu&aacute;ch &Aacute;nh c&ugrave;ng thương hiệu mỹ phẩm ri&ecirc;ng&nbsp;<strong>LEMONADE</strong>&nbsp;đ&atilde; vừa cho tung ra d&ograve;ng son l&igrave; Lemonade<strong>&nbsp;Matte Addict Lipstick&nbsp;</strong>với 5 t&ocirc;ng m&agrave;u thời thượng cho n&agrave;ng tha hồ lựa chọn đ&oacute; nh&eacute;!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-li-lemonade-1.jpg\" style=\"height:550px; width:550px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Matte Addict Lipstick</strong>&nbsp;l&agrave; son l&igrave; dạng thỏi l&acirc;u tr&ocirc;i bật m&agrave;u v&agrave; b&aacute;m l&acirc;u, chất son mềm mượt như bơ đồng thời l&ecirc;n m&agrave;u chuẩn ngay c&aacute;i chạm đầu ti&ecirc;n.&nbsp;</p>\r\n\r\n<p>Kh&ocirc;ng chỉ l&agrave; một thỏi <strong>son l&igrave;</strong>&nbsp;đơn thuần,&nbsp;<strong>Matte Addict Lipstick</strong>&nbsp;c&ograve;n gi&uacute;p bảo vệ m&ocirc;i với chỉ số chống nắng ho&agrave;n hảo v&agrave; dưỡng m&ocirc;i lu&ocirc;n mềm mịn nhờ c&oacute; thần dược dưỡng ẩm từ tinh dầu Argan.&nbsp;</p>\r\n\r\n<p>Như vậy l&agrave; bạn sẽ sở hữu được đ&ocirc;i m&ocirc;i vừa l&igrave; l&acirc;u tr&ocirc;i vừa v&ocirc; c&ugrave;ng mềm mịn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/1705_-_andybui8922_copy.jpg\" style=\"height:440px; width:550px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>LEMONADE</strong>&nbsp;l&agrave; thương hiệu makeup&nbsp;được&nbsp;đặc biệt tạo ra như một m&oacute;n qu&agrave; d&agrave;nh ri&ecirc;ng cho phụ nữ Việt Nam của &quot;Ph&ugrave; thủy Makeup Việt Nam&quot; - Qu&aacute;ch &Aacute;nh. Với kinh nghiệm l&agrave;m việc trong lĩnh vực&nbsp;<strong><a href=\"https://hasaki.vn/danh-muc/trang-diem-c23.html\" target=\"_blank\">trang điểm</a></strong>&nbsp;gần 10 năm của m&igrave;nh, Qu&aacute;ch &Aacute;nh đ&atilde; nghiền ngẫm, nghi&ecirc;n cứu v&agrave; v&agrave; cho ra đời &quot;đứa con tinh thần đầu ti&ecirc;n&quot; của m&igrave;nh l&agrave; d&ograve;ng&nbsp;<strong>Son Kem L&igrave; 2 Đầu LEMONADE&nbsp;</strong><strong>Perfect Couple Lip</strong>. Sản phẩm ng&agrave;y c&agrave;ng được y&ecirc;u th&iacute;ch bởi c&aacute;c bạn g&aacute;i nhờ th&agrave;nh phần v&agrave; chất lượng h&agrave;ng đầu, nhưng lại c&oacute; mức gi&aacute; qu&aacute; ư &quot;hạt dẻ&quot;.&nbsp;<strong>LEMONADE</strong><strong>&nbsp;</strong>hứa hẹn sẽ l&agrave; một trong những thương hiệu mỹ phẩm Việt Nam được giới trẻ ưa chuộng trong tương lai.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-li-lemonade-2.jpg\" style=\"height:368px; width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hiện sản phẩm đ&atilde; c&oacute; mặt&nbsp;với 5 t&ocirc;ng m&agrave;u thời thượng được đặt t&ecirc;n&nbsp;dựa theo 5 điều c&oacute; thể khiến con g&aacute;i &quot;nghiện&quot;:</p>\r\n\r\n<p><strong>A01 Boy - M&agrave;u Hồng Đỏ</strong></p>\r\n\r\n<p><strong>A02 Shopping - M&agrave;u Đỏ Đất</strong></p>\r\n\r\n<p><strong>A03 Instagram - M&agrave;u Cam Đất</strong></p>\r\n\r\n<p><strong>A04 Milk Tea - M&agrave;u Cam N&acirc;u</strong></p>\r\n\r\n<p><strong>A05 Selfie - M&agrave;u Đỏ Tươi</strong></p>\r\n\r\n<p>M&agrave;u<strong>&nbsp;A01. Boy</strong>&nbsp;l&agrave; một tone&nbsp;<strong>HỒNG ĐỎ</strong>&nbsp;dịu d&agrave;ng ho&agrave;n hảo cho c&aacute;c c&ocirc; n&agrave;ng khi hẹn h&ograve; c&ugrave;ng &ldquo;Crush&rdquo;, tươi tắn v&agrave; dễ d&ugrave;ng cho mọi tone da.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-li-lemonade-3.jpg\" style=\"height:550px; width:550px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-li-lemonade-4.jpg\" style=\"height:550px; width:550px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave;u&nbsp;<strong>A02. Shopping</strong>&nbsp;l&agrave; tone&nbsp;<strong>ĐỎ ĐẤT</strong>&nbsp;lạnh kh&ocirc;ng chỉ nổi bật, t&ocirc;n da m&agrave; c&ograve;n cực k&igrave; s&aacute;ng men răng. Đ&acirc;y l&agrave; tone m&agrave;u được nghi&ecirc;n cứu v&agrave; lựa chọn kĩ c&agrave;ng để cực ph&ugrave; hợp với tone da ngăm v&agrave; trung b&igrave;nh s&aacute;ng của con g&aacute;i Việt Nam.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-li-lemonade-5.jpg\" style=\"height:550px; width:550px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-li-lemonade-6.jpg\" style=\"height:550px; width:550px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave;u&nbsp;<strong>A03. Instagram</strong>&nbsp;l&agrave; tone&nbsp;<strong>CAM ĐẤT</strong>&nbsp;c&aacute; t&iacute;nh v&agrave; rực rỡ cho m&ugrave;a h&egrave; n&agrave;y. Sự h&ograve;a quyện giữa sắc cam tươi v&agrave; m&agrave;u n&acirc;u đất gi&uacute;p &ldquo;Instagram&rdquo; dễ d&agrave;ng ph&ugrave; hợp cho bất k&igrave; make up look n&agrave;o. Chỉ bằng việc t&ocirc; m&agrave;u son n&agrave;y, bạn sẽ thật sự trở th&agrave;nh một HOT Instagram cực chất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-li-lemonade-7.jpg\" style=\"height:550px; width:550px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-li-lemonade-8.jpg\" style=\"height:550px; width:550px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave;u&nbsp;<strong>A04. Milk Tea</strong>&nbsp;l&agrave; tone&nbsp;<strong>CAM N&Acirc;U</strong>&nbsp;đang cực k&igrave; thịnh h&agrave;nh v&agrave; mọi c&ocirc; g&aacute;i đều n&ecirc;n c&oacute; sắc th&aacute;i n&agrave;y. Sắc Đỏ tươi tắn, sắc Cam th&ecirc;m nổi bật v&agrave; h&ograve;a trộn với tone N&acirc;u Gạch sẽ trung h&ograve;a được cả m&agrave;u son. Đ&acirc;y cũng l&agrave; m&agrave;u son được Qu&aacute;ch &Aacute;nh lựa chọn kĩ c&agrave;ng nhất để gi&uacute;p bạn đẹp nhất ngay khi mặt mộc.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-li-lemonade-9.jpg\" style=\"height:550px; width:550px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-li-lemonade-10.jpg\" style=\"height:550px; width:550px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave;u&nbsp;<strong>A05. Selfie</strong>&nbsp;l&agrave; tone&nbsp;<strong>ĐỎ TƯƠI</strong>&nbsp;m&agrave; mọi c&ocirc; g&aacute;i đều phải c&oacute; &iacute;t nhất một thỏi. Đ&acirc;y l&agrave; tone son tươi s&aacute;ng, nổi bật v&agrave; ph&ugrave; hợp mọi l&uacute;c mọi nơi, kể cả khi bạn đi học, đi chơi, đi l&agrave;m đều c&oacute; thể sử dụng, v&agrave; chắc chắn rằng với m&agrave;u son n&agrave;y bạn sẽ c&oacute; những bức ảnh &ldquo;Selfie&rdquo; tuyệt đẹp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-li-lemonade-11.jpg\" style=\"height:550px; width:550px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-li-lemonade-12.jpg\" style=\"height:550px; width:550px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bảo quản:</strong></p>\r\n\r\n<p>- Bảo quản nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t.</p>\r\n\r\n<p>- Tr&aacute;nh &aacute;nh nắng trực tiếp &aacute;nh nắng mặt trời.</p>\r\n\r\n<p>- Đậy k&iacute;n nắp sau khi sử dụng.</p>\r\n\r\n<p><strong>Quy c&aacute;ch sản phẩm:&nbsp;</strong>3.8g</p>\r\n\r\n<p><strong>Thương hiệu:&nbsp;</strong>LEMONADE&nbsp;</p>\r\n\r\n<p><strong>Xuất xứ:&nbsp;</strong>Việt Nam</p>\r\n\r\n<p><strong>Sản xuất tại:&nbsp;</strong>H&agrave;n Quốc</p>\r\n', 1, 2, 13),
(43, 'Son Kem Lì 2 Đầu LEMONADE', 'son03', '270000', 36, '1655725281_Son4.png', '<p><strong><strong><strong><strong>Son Kem L&igrave; 2 Đầu LEMONADE&nbsp;</strong><strong>Perfect Couple Lip</strong></strong></strong></strong>&nbsp;l&agrave; d&ograve;ng son đầu ti&ecirc;n được ra mắt từ <strong>LEMONADE</strong>, được xem như &quot;đứa con tinh thần&quot; của&nbsp;&quot;ph&ugrave; thủy makeup Việt Nam&quot; Qu&aacute;ch &Aacute;nh.&nbsp;</p>\r\n', '<p>&nbsp;Sau hơn 6 th&aacute;ng li&ecirc;n tục thử nghiệm v&agrave; lựa chọn kĩ c&agrave;ng&nbsp;từ gần 500 mẫu son m&agrave; Qu&aacute;ch &Aacute;nh đ&atilde; thử,&nbsp;<strong><strong><strong><strong>LEMONADE&nbsp;</strong><strong>Perfect Couple Lip&nbsp;</strong></strong></strong></strong>đ&atilde; ra đời với 5 t&ocirc;ng m&agrave;u&nbsp;son kem l&igrave; t&ocirc;n da nhất,&nbsp;được đặt t&ecirc;n theo 5 hương vị truyền thống để l&agrave;m n&ecirc;n ly tr&agrave; chanh tuyệt hảo &quot;LEMONADE&quot;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-kem-li-2-dau-lemonade-1.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-kem-li-2-dau-lemonade-4.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&uacute;ng như t&ecirc;n gọi của c&acirc;y son,&nbsp;<strong>LEMONADE&nbsp;</strong><strong>Perfect Couple Lip</strong>&nbsp;được thiết kế gồm 2 phần, phần tr&ecirc;n&nbsp;l&agrave; Lip Filler với Shade hồng nude trong suốt, c&oacute; c&ocirc;ng dụng giống như son dưỡng, được sử dụng trước khi thoa lớp m&agrave;u.&nbsp;Shade hồng nude trong suốt n&agrave;y sẽ dưỡng&nbsp;ẩm,&nbsp;đồng thời hỗ trợ l&agrave;m mờ c&aacute;c r&atilde;nh m&ocirc;i rất tốt. Đặc biệt, th&agrave;nh phần chiết xuất Hyaluronic Acid c&oacute; trong son c&ograve;n gi&uacute;p tạo được hiệu ứng căng mọng v&agrave; gi&uacute;p cho m&agrave;u son l&ecirc;n m&ocirc;i&nbsp;được r&otilde; m&agrave;u v&agrave; chuẩn n&eacute;t nữa&nbsp;đấy.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-kem-li-2-dau-lemonade-6.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đầu c&ograve;n lại l&agrave; son kem l&igrave; với vỏ ngo&agrave;i trong suốt, khi mới thoa l&ecirc;n m&ocirc;i bạn sẽ cảm nhận r&otilde; được chất satin nhưng &iacute;t ph&uacute;t sau son sẽ kh&ocirc; lại v&agrave; l&igrave; hơn. Điều đặc biệt của&nbsp;<strong>LEMONADE&nbsp;Perfect Couple Lip&nbsp;</strong>ch&iacute;nh l&agrave; tuy đ&acirc;y l&agrave; dạng son kem l&igrave;, nhưng chất son l&ecirc;n m&ocirc;i cực kỳ mướt mịn chứ kh&ocirc;ng hề g&acirc;y kh&ocirc; m&ocirc;i, vệt son loang lỗ hay g&acirc;y cảm gi&aacute;c nặng m&ocirc;i đ&acirc;u nh&eacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-kem-li-2-dau-lemonade-5.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>LEMONADE&nbsp;</strong><strong>Perfect</strong>&nbsp;<strong>Couple</strong>&nbsp;<strong>Lip</strong>&nbsp;tại&nbsp;<strong>Hasaki&nbsp;</strong>hiện c&oacute;&nbsp;5 sắc son t&ocirc;n da v&agrave; rất được y&ecirc;u th&iacute;ch gi&uacute;p bạn vừa trở n&ecirc;n xinh xắn vừa hợp xu hướng:</p>\r\n\r\n<p>-&nbsp;<strong>M&agrave;u N&acirc;u Cam Đất 01 Sugar</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/quynhtrang/Trang/son-kem-li-2-dau-lemonade4.jpg\" style=\"width:800px\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>- M&agrave;u Hồng Đất 02 Ice</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/Dai/son-kem-li-2-dau-lemonade-mau-hong-dat-02-ice4.jpg\" style=\"width:800px\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>-<strong>&nbsp;M&agrave;u Cam Đỏ Đất 03 Salt</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/quynhtrang/Trang/son-kem-li-2-dau-lemonade6.jpg\" style=\"width:800px\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>-<strong>&nbsp;M&agrave;u Cam Đất 04 Honey</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/quynhtrang/Trang/son-kem-li-2-dau-lemonade7.jpg\" style=\"width:800px\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>-&nbsp;<strong>M&agrave;u Đỏ Đất 05 Tea</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/quynhtrang/Trang/son-kem-li-2-dau-lemonade8.jpg\" style=\"width:800px\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>C&ocirc;ng dụng:</strong></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Gi&uacute;p l&agrave;m đầy r&atilde;nh m&ocirc;i v&agrave; cấp ẩm cho m&ocirc;i hiệu quả với th&agrave;nh phần chiết xuất&nbsp;Hyaluronic Acid.</p>\r\n	</li>\r\n	<li>\r\n	<p>Gi&uacute;p tạo được hiệu ứng m&ocirc;i căng mọng, mềm mịn, kh&ocirc;ng kh&ocirc; v&agrave; lộ v&acirc;n m&ocirc;i khi sử dụng với bất k&igrave; kem lỳ n&agrave;o,&nbsp;gi&uacute;p sắc m&agrave;u son l&ecirc;n m&ocirc;i&nbsp;đ&uacute;ng m&agrave;u, sắc n&eacute;t v&agrave; giữ được m&agrave;u l&acirc;u.</p>\r\n	</li>\r\n	<li>\r\n	<p>Gi&uacute;p gương mặt bạn trở n&ecirc;n tươi tắn, rạng rỡ hơn.</p>\r\n	</li>\r\n	<li>\r\n	<p>Gi&uacute;p mang lại hiệu quả dưỡng m&ocirc;i hồng mềm mịn khi sử dụng thường xuy&ecirc;n.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-kem-li-2-dau-lemonade-3.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-kem-li-2-dau-lemonade-2.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Bảo quản:</strong></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Bảo quản nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t.</p>\r\n	</li>\r\n	<li>\r\n	<p>Tr&aacute;nh &aacute;nh nắng trực tiếp &aacute;nh nắng mặt trời.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đậy k&iacute;n nắp sau khi sử dụng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Quy c&aacute;ch sản phẩm:&nbsp;</strong>Phần Lip Filler (3.5ml) v&agrave; Lip Cream (4ml)</p>\r\n\r\n<p><strong>Thương hiệu:&nbsp;</strong>LEMONADE&nbsp;</p>\r\n\r\n<p><strong>Xuất xứ:&nbsp;</strong>Việt Nam</p>\r\n\r\n<p><strong>Sản xuất tại:&nbsp;</strong>H&agrave;n Quốc</p>\r\n', 1, 2, 13),
(44, 'Son Lót Dưỡng Môi LEMONADE', 'son04', '120000', 50, '1655725425_son6.png', '<p>Nỗi khổ m&ocirc;i mỏng, dễ kh&ocirc; hay lộ v&acirc;n lu&ocirc;n khiến c&aacute;c n&agrave;ng lăn tăn khi quyết định sử dụng son l&igrave;. Đừng lo lắng nữa v&igrave; từ nay đ&atilde; c&oacute;&nbsp;<strong>Son L&oacute;t Dưỡng Đầy M&ocirc;i&nbsp;</strong>từ thương hiệu<strong><a href=\"https://hasaki.vn/thuong-hieu/lemonade.html\" target=\"_blank\">&nbsp;</a>LEMONADE,&nbsp;</strong>&quot;vũ kh&iacute; b&iacute; mật&quot; gi&uacute;p cung&nbsp;cấp độ ẩm, l&agrave;m đầy r&atilde;nh m&ocirc;i, cho m&ocirc;i mịn như kem v&agrave; son l&ecirc;n m&agrave;u &quot;chuẩn kh&ocirc;ng cần chỉnh&quot;.&nbsp;</p>\r\n', '<p>Sản phẩm chắc chắn sẽ l&agrave; một &quot;bảo bối&quot; bất li th&acirc;n lu&ocirc;n phải c&oacute; trong t&uacute;i m&agrave; c&aacute;c c&ocirc; n&agrave;ng đam m&ecirc; son kh&ocirc;ng n&ecirc;n bỏ qua.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/UYEN/son-lot-duong-day-moi-lemonade_2.jpg\" style=\"height:700px; width:700px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>LEMONADE</strong>&nbsp;l&agrave; thương hiệu makeup&nbsp;được&nbsp;đặc biệt tạo ra như một m&oacute;n qu&agrave; d&agrave;nh ri&ecirc;ng cho phụ nữ Việt Nam của &quot;Ph&ugrave; thủy Makeup Việt Nam&quot; - Qu&aacute;ch &Aacute;nh. Với kinh nghiệm l&agrave;m việc trong lĩnh vực trang điểm&nbsp;gần 10 năm của m&igrave;nh, Qu&aacute;ch &Aacute;nh đ&atilde; nghiền ngẫm, nghi&ecirc;n cứu v&agrave; v&agrave; cho ra đời &quot;đứa con tinh thần đầu ti&ecirc;n&quot; của m&igrave;nh l&agrave; d&ograve;ng&nbsp;<strong>Son Kem L&igrave; 2 Đầu LEMONADE&nbsp;</strong><strong>Perfect Couple Lip</strong>. Sản phẩm ng&agrave;y c&agrave;ng được y&ecirc;u th&iacute;ch bởi c&aacute;c bạn g&aacute;i nhờ th&agrave;nh phần v&agrave; chất lượng h&agrave;ng đầu, nhưng lại c&oacute; mức gi&aacute; qu&aacute; ư &quot;hạt dẻ&quot;.&nbsp;<strong>LEMONADE</strong><strong>&nbsp;</strong>hứa hẹn sẽ l&agrave; một trong những thương hiệu mỹ phẩm Việt Nam được giới trẻ ưa chuộng trong tương lai.&nbsp;</p>\r\n\r\n<p>Với shade hồng nude trong suốt, th&agrave;nh phần chiết xuất Hyaluronic Acid v&agrave; Vitamin E,&nbsp;<strong>Son L&oacute;t Dưỡng Đầy M&ocirc;i LEMONADE&nbsp;</strong>hiện đ&atilde; c&oacute; mặt sẽ h&ocirc; biến bờ m&ocirc;i bạn trở n&ecirc;n căng mọng, hồng h&agrave;o, son l&ecirc;n đ&uacute;ng m&agrave;u, kh&ocirc;ng lộ v&acirc;n &quot;k&eacute;m duy&ecirc;n&quot;, gi&uacute;p bờ m&ocirc;i n&agrave;ng lu&ocirc;n quyến rũ v&agrave; tỏa s&aacute;ng.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/UYEN/son-lot-duong-day-moi-lemonade_13_.jpg\" style=\"height:429px; width:700px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:</strong></p>\r\n\r\n<p>- L&agrave;m đầy r&atilde;nh m&ocirc;i v&agrave; cấp ẩm cho m&ocirc;i hiệu quả suốt 12 giờ.</p>\r\n\r\n<p>- Tạo được hiệu ứng m&ocirc;i căng mọng, mềm mịn, kh&ocirc;ng kh&ocirc; v&agrave; lộ v&acirc;n m&ocirc;i, gi&uacute;p sắc son bật l&ecirc;n đ&uacute;ng m&agrave;u với bất k&igrave; loại son kem lỳ n&agrave;o.</p>\r\n\r\n<p>- Hiệu quả dưỡng m&ocirc;i hồng mềm mịn khi sử dụng thường xuy&ecirc;n trong 30 ng&agrave;y.</p>\r\n\r\n<p>- C&oacute; thể sử dụng như kem l&oacute;t mắt, gi&uacute;p tăng độ b&aacute;m v&agrave; l&ecirc;n m&agrave;u của phấn mắt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/UYEN/son-lot-duong-day-moi-lemonade_10_.jpg\" style=\"height:700px; width:700px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bảo quản:</strong></p>\r\n\r\n<p>- Bảo quản nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t.</p>\r\n\r\n<p>- Tr&aacute;nh &aacute;nh nắng mặt trời trực tiếp.</p>\r\n\r\n<p>- Đậy k&iacute;n nắp sau khi sử dụng.</p>\r\n\r\n<p><strong>Xuất xứ:&nbsp;</strong>Việt Nam</p>\r\n\r\n<p><strong>Sản xuất tại:&nbsp;</strong>H&agrave;n Quốc</p>\r\n\r\n<p><strong>Thương hiệu:&nbsp;</strong>LEMONADE&nbsp;</p>\r\n', 1, 2, 13),
(45, 'Nước Tẩy Trang Maybelline 150ml', 'TT07', '119000', 36, '1655725596_TT07.png', '<p><strong>Nước TẨY TRANG MẮT M&Ocirc;I&nbsp;Maybelline Lip &amp; Eyes Make Up Remover</strong></p>\r\n\r\n<p>Đối với những c&ocirc; n&agrave;ng thường xuy&ecirc;n trang điểm&nbsp;th&igrave; một chai nước tẩy trang d&agrave;nh cho v&ugrave;ng mắt v&agrave; m&ocirc;i l&agrave; thứ kh&ocirc;ng thể thiếu. Những vị tr&iacute; n&agrave;y vốn c&oacute; l&agrave;n da mỏng v&agrave; nhạy cảm hơn những v&ugrave;ng da kh&aacute;c, v&igrave; vậy n&oacute; cần được bảo vệ tốt hơn. Maybelline<strong>&nbsp;Make Up Remover Eye &amp; Lip</strong>&nbsp;l&agrave; nước tẩy trang d&agrave;nh ri&ecirc;ng cho v&ugrave;ng da nhạy cảm l&agrave; mắt v&agrave; m&ocirc;i, gi&uacute;p tẩy sạch lớp trang điểm m&agrave; vẫn an to&agrave;n cho da. Sản phẩm&nbsp;nhẹ dịu nhưng vẫn đảm bảo tẩy sạch c&aacute;c vết trang điểm cứng đầu nhất như mascara kh&ocirc;ng tr&ocirc;i, son l&acirc;u phai&hellip; trả lại cho bạn l&agrave;n da &ldquo;mộc&rdquo; tự nhi&ecirc;n.</p>\r\n', '<p><strong>Maybeline</strong>&nbsp;l&agrave; thương hiệu mỹ phẩm nổi tiếng tại mỹ đ&atilde; cho ra đời nhiều d&ograve;ng sản phẩm được nhiều kh&aacute;ch h&agrave;ng từ giới nổi tiếng đến những người phụ nữ b&igrave;nh thường biết đến. Lấy cảm hứng từ sự tự tin của những người phụ nữ t&agrave;i năng,&nbsp;<strong>Maybeline</strong>&nbsp;cung cấp cho người d&ugrave;ng những c&ocirc;ng thức khoa học ti&ecirc;n tiến mang đến xu hướng mới, dễ d&agrave;ng sử dụng với gi&aacute; th&agrave;nh hợp l&yacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/nhphuong/PhuongSmall/nuoc-tay-trang-mat-moi-40ml-maybelline1.jpg\" style=\"height:450px; width:450px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Nước Tẩy Trang Mắt M&ocirc;i Maybelline Lip &amp; Eyes Make Up Remover</strong>&nbsp;kết hợp nhiều th&agrave;nh phần l&agrave;m mềm da nhẹ nh&agrave;ng như Rose water, Kawa extract&hellip; c&oacute; t&aacute;c dụng tẩy trang, l&agrave;m sạch da v&agrave; nu&ocirc;i dưỡng đặc biệt cho v&ugrave;ng da quanh mắt, l&ocirc;ng mi, l&ocirc;ng m&agrave;y v&agrave; đ&ocirc;i m&ocirc;i. C&aacute;c hoạt chất trong sản phẩm sẽ gi&uacute;p tẩy sạch c&aacute;c vết trang điểm cứng đầu nhất như mascara kh&ocirc;ng tr&ocirc;i, son l&acirc;u phai&hellip; đồng thời đưa v&ugrave;ng da quanh mắt v&agrave; m&ocirc;i trở về trạng th&aacute;i tự nhi&ecirc;n, mềm mại.</p>\r\n\r\n<p>Sản phẩm được chứng nhận l&agrave; kh&ocirc;ng g&acirc;y ra hiện tượng k&iacute;ch ứng da v&agrave; lu&ocirc;n giữ được sự mềm mại cho v&ugrave;ng da quanh mắt v&agrave; m&ocirc;i sau khi tẩy trang.</p>\r\n\r\n<p><strong>Loại dap ph&ugrave; hợp:</strong></p>\r\n\r\n<p>Sản phẩm th&iacute;ch hợp với mọi loại da.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/nhphuong/PhuongSmall/nuoc-tay-trang-mat-moi-40ml-maybelline.jpg\" style=\"height:257px; width:630px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&ocirc;ng dụng:</strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Loại bỏ lớp trang điểm cứng đầu ở v&ugrave;ng mắt, m&ocirc;i.</p>\r\n	</li>\r\n	<li>\r\n	<p>Bổ sung th&agrave;nh phần dưỡng ẩm nu&ocirc;i dưỡng l&agrave;n da mịn m&agrave;ng.</p>\r\n	</li>\r\n	<li>\r\n	<p>Mang đến l&agrave;n da sạch khỏe, th&ocirc;ng tho&aacute;ng, rạng rỡ tr&ocirc;ng thấy.</p>\r\n	</li>\r\n	<li>\r\n	<p>Sản phẩm kh&ocirc;ng g&acirc;y k&iacute;ch ứng da.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;<img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/nhphuong/PhuongSmall/sua-rua-mat-va-tay-trang-cho-moi-loai-da-christian-lenart_2.jpg\" style=\"height:671px; width:450px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bảo quản:</strong></p>\r\n\r\n<p>Nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t.</p>\r\n\r\n<p>Tr&aacute;nh &aacute;nh nắng trực tiếp, nơi c&oacute; nhiệt độ cao hoặc ẩm ướt.</p>\r\n\r\n<p>Đậy nắp k&iacute;n sau khi sử dụng.</p>\r\n\r\n<p><strong>Dung t&iacute;ch:</strong>&nbsp;40ml &amp; 150ml</p>\r\n\r\n<p><strong>Thương hiệu:&nbsp;</strong>Maybelline</p>\r\n\r\n<p><strong>Xuất xứ:</strong>&nbsp;Mỹ.</p>\r\n', 1, 8, 10);
INSERT INTO `tbl_sanpham` (`id_sanpham`, `tensanpham`, `masp`, `giasp`, `soluong`, `hinhanh`, `tomtat`, `noidung`, `tinhtrang`, `id_danhmuc`, `id_hieusp`) VALUES
(46, 'Son Lì Maybelline Đỏ Gạch ', 'son05', '149000', 40, '1655725709_SON2.jpg', '<p><strong>Son L&igrave; Mịn M&ocirc;i Si&ecirc;u Nhẹ Maybelline&nbsp;Color Sensational Ultimatte</strong>&nbsp;l&agrave; d&ograve;ng son l&igrave;&nbsp;cao cấp mới nhất từ&nbsp;<a href=\"https://hasaki.vn/thuong-hieu/maybelline.html\" target=\"_blank\">thương hiệu Maybelline New York</a>,&nbsp;cho bạn 3 t&iacute;nh năng vượt trội &quot;Mịn - Mờ - Nhẹ t&ecirc;nh&quot;.&nbsp;Với c&ocirc;ng nghệ l&agrave;m mờ r&atilde;nh m&ocirc;i mang đến lớp finish mịn mờ sang trọng kết hợp c&ocirc;ng nghệ ultrafine powder cho son mịn l&igrave; nhẹ t&ecirc;nh.&nbsp;</p>\r\n', '<p>&nbsp;Thiết kế đầu son h&igrave;nh giọt sương nhỏ gi&uacute;p son dễ d&agrave;ng lướt nhẹ v&agrave; chuẩn m&agrave;u tr&ecirc;n m&ocirc;i bạn. Đặc biệt, vỏ son &ldquo;mảnh mai thon gọn&rdquo; c&ugrave;ng m&agrave;u sắc bắt mắt, khơi gợi hơi thở sang trọng, thời thượng v&agrave; trẻ trung.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Son Lì Mịn Môi Siêu Nhẹ Cao Cấp Maybelline Color Sensational Ultimatte\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-li-maybelline-min-moi-sieu-nhe-1-7g-1.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Son Lì Mịn Môi Siêu Nhẹ Cao Cấp Maybelline Color Sensational Ultimatte 1.7g\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-li-maybelline-min-moi-sieu-nhe-1-7g-2.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hiện&nbsp;<strong>Son L&igrave; Mịn M&ocirc;i Si&ecirc;u Nhẹ Maybelline&nbsp;Color Sensational Ultimatte&nbsp;</strong>đ&atilde; c&oacute; mặt<strong>&nbsp;</strong>với 4 tone m&agrave;u hot nhất cho bạn lựa chọn:</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>299 - More Scarlet:</strong>&nbsp;Đỏ thuần</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>499 - More Blush:</strong>&nbsp;Hồng đất</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>799 - More Taupe:</strong>&nbsp;Cam ngả đất</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>899 - More Rust:&nbsp;</strong>Đỏ gạch</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Son Lì Mịn Môi Siêu Nhẹ Maybelline Color Sensational Ultimatte 299 More Scarlet Màu Đỏ Thuần\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-li-maybelline-min-moi-sieu-nhe-1-7g-3.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Son Lì Mịn Môi Siêu Nhẹ Maybelline Color Sensational Ultimatte 899 More Rust Đỏ Gạch\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-li-maybelline-min-moi-sieu-nhe-1-7g-4.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Son Lì Mịn Môi Siêu Nhẹ Maybelline Color Sensational Ultimatte với các tone thời thượng, tôn da ngay tức thì mà không cần trang điểm cầu kỳ chuyên nghiệp.\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-li-maybelline-min-moi-sieu-nhe-1-7g-5.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Son Lì Mịn Môi Siêu Nhẹ Maybelline Color Sensational Ultimatte với 7 tone màu thời thượng cho bạn lựa chọn\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-li-maybelline-min-moi-sieu-nhe-1-7g-6.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Ưu thế nổi bật:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>Maybelline&nbsp;Color Sensational Ultimatte</strong>&nbsp;sở hữu chất son mềm, mượt, mịn, kh&ocirc;ng để lộ v&acirc;n m&ocirc;i.</p>\r\n	</li>\r\n	<li>\r\n	<p>C&ocirc;ng nghệ l&agrave;m mờ r&atilde;nh m&ocirc;i cho hiệu ứng mịn l&igrave; - mờ ảo - nhẹ t&ecirc;nh.</p>\r\n	</li>\r\n	<li>\r\n	<p>Chứa<strong>&nbsp;tinh dầu Jojoba</strong>&nbsp;gi&uacute;p dưỡng ẩm cho m&ocirc;i th&ecirc;m mềm mịn, hạn chế t&igrave;nh trạng kh&ocirc; nứt m&ocirc;i.</p>\r\n	</li>\r\n	<li>\r\n	<p>Khả năng l&ecirc;n m&agrave;u chuẩn chỉ sau 1 lần lướt c&ugrave;ng độ bền m&agrave;u cao, giữ sắc m&ocirc;i lu&ocirc;n tươi tắn suốt nhiều giờ liền.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đầu son h&igrave;nh giọt sương lướt &ecirc;m tr&ecirc;n m&ocirc;i.</p>\r\n	</li>\r\n	<li>\r\n	<p>Bảng m&agrave;u&nbsp;son với 7 tone thời thượng, t&ocirc;n da ngay tức th&igrave; m&agrave; kh&ocirc;ng cần trang điểm cầu kỳ chuy&ecirc;n nghiệp.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Ưu điểm nổi bật của Son Lì Mịn Môi Siêu Nhẹ Maybelline Color Sensational Ultimatte\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-li-maybelline-min-moi-sieu-nhe-1-7g-7.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Son Lì Mịn Môi Siêu Nhẹ Maybelline Color Sensational Ultimatte hiện đã có mặt tại Hasaki\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-li-maybelline-min-moi-sieu-nhe-1-7g-8.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Bảo quản:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Bảo quản nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp hoặc nhiệt độ cao, nơi ẩm ướt.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&ocirc;ng&nbsp;vặn son&nbsp;l&ecirc;n qu&aacute; cao.&nbsp;Vặn son xuống v&agrave; đậy chặt nắp&nbsp;sau&nbsp;khi sử dụng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2>Th&ocirc;ng số sản phẩm:</h2>\r\n\r\n<p><strong>Thương hiệu:&nbsp;</strong>Maybelline New York</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Mỹ</p>\r\n\r\n<p><strong>Nơi sản xuất:</strong>&nbsp;Trung Quốc</p>\r\n\r\n<p><strong>Ng&agrave;y sản xuất:</strong>&nbsp;Xem tr&ecirc;n bao b&igrave; sản phẩm</p>\r\n\r\n<p><strong>Hạn sử dụng:</strong>&nbsp;3 năm kể từ ng&agrave;y sản xuất.</p>\r\n\r\n<p><strong>Dung t&iacute;ch:</strong>&nbsp;1.7g</p>\r\n', 1, 2, 10),
(47, 'Son Kem Lì Maybelline ', 'son07', '189000', 40, '1655725837_son7.png', '<p><strong>Son Kem L&igrave; 16H L&acirc;u Tr&ocirc;i&nbsp;Maybelline New York Super Stay Matte Ink Lipstick</strong>&nbsp;l&agrave; d&ograve;ng&nbsp;<strong>&nbsp;son l&igrave;&nbsp;</strong>si&ecirc;u phẩm đến từ&nbsp;<strong><a href=\"https://hasaki.vn/thuong-hieu/maybelline.html\" target=\"_blank\">thương hiệu mỹ phẩm trang điểm Maybelline New York</a>&nbsp;</strong>nổi tiếng của Mỹ&nbsp;khiến h&agrave;ng triệu c&ocirc; g&aacute;i say đắm, với&nbsp;c&ocirc;ng nghệ mực đ&agrave;n hồi độc quyền mang lại chất son chuẩn mịn l&igrave;, l&acirc;u tr&ocirc;i l&ecirc;n đến 16h, gi&uacute;p m&ocirc;i l&ecirc;n m&agrave;u chuẩn suốt cả ng&agrave;y d&agrave;i, kh&ocirc;ng lem, kh&ocirc;ng tr&ocirc;i v&agrave; kh&ocirc;ng g&acirc;y kh&ocirc; m&ocirc;i. Bảng m&agrave;u v&ocirc; c&ugrave;ng&nbsp;đa dạng gi&uacute;p c&aacute;c n&agrave;ng c&oacute; thể thỏa th&iacute;ch lựa chọn.</p>\r\n', '<p>Đối với c&aacute;c t&iacute;n đồ trang điểm&nbsp;th&igrave; một trong những ti&ecirc;u ch&iacute; h&agrave;ng đầu khi lựa chọn son m&ocirc;i đ&oacute; ch&iacute;nh l&agrave; khả năng bền m&agrave;u l&acirc;u tr&ocirc;i. Chẳng c&ocirc; n&agrave;ng n&agrave;o th&iacute;ch việc cứ phải dặm đi dặm lại son m&ocirc;i nhiều lần trong ng&agrave;y, hoặc uống xong một cốc tr&agrave; sữa l&agrave; son m&ocirc;i tr&ocirc;i sạch đ&acirc;u đ&uacute;ng kh&ocirc;ng n&agrave;o? Hiểu được nỗi l&ograve;ng của ph&aacute;i đẹp, Maybelline New York đ&atilde; cho ra đời d&ograve;ng son kem mịn l&igrave;&nbsp;<strong>Super Stay Matte Ink Lipstick</strong><strong>,&nbsp;</strong>ứng dụng c&ocirc;ng nghệ mực đ&agrave;n hồi độc quyền gi&uacute;p son giữ m&agrave;u đến 16H, kh&ocirc;ng lem, kh&ocirc;ng tr&ocirc;i v&agrave; kh&ocirc;ng g&acirc;y kh&ocirc; m&ocirc;i, cho n&agrave;ng tha hồ ăn uống v&agrave; vui chơi suốt ng&agrave;y d&agrave;i m&agrave; vẫn tự tin với sắc m&ocirc;i đẹp ho&agrave;n hảo.</p>\r\n\r\n<p><strong><strong>Super Stay Matte Ink Lipstick</strong>&nbsp;</strong>cho khả năng l&ecirc;n m&agrave;u chuẩn chỉ sau một lần lướt son tr&ecirc;n m&ocirc;i, đồng thời che phủ ho&agrave;n hảo đến từng kẽ m&ocirc;i của bạn, mang lại bờ m&ocirc;i mịn mượt như nhung, căng tr&agrave;n sức sống v&agrave; quyến rũ. Độ chống nước cao gi&uacute;p son kh&ocirc;ng d&iacute;nh tr&ecirc;n th&agrave;nh cốc, thức ăn hoặc quần &aacute;o, kh&ocirc;ng tr&ocirc;i khi đi mưa hay đi bơi.</p>\r\n\r\n<p>Từng thỏi son&nbsp;<strong>Super Stay Matte Ink Lipstick</strong><strong>&nbsp;</strong>được thiết kế nhỏ xinh vừa vặn trong l&ograve;ng b&agrave;n tay, mang đậm chất ri&ecirc;ng của thương hiệu Maybelline New York. M&agrave;u son b&ecirc;n trong giống hệt với m&agrave;u vỏ b&ecirc;n ngo&agrave;i, gi&uacute;p c&aacute;c n&agrave;ng dễ d&agrave;ng chọn được t&ocirc;ng m&agrave;u son y&ecirc;u th&iacute;ch m&agrave; kh&ocirc;ng cần phải mở nắp. Đầu cọ thoa son được thiết kế h&igrave;nh mũi t&ecirc;n, tạo lớp son mỏng nhẹ v&agrave; gi&uacute;p dễ t&ocirc; son đều, phủ m&agrave;u đến tận kẽ m&ocirc;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-kem-li-maybelline-16h-lau-troi-2.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>H&atilde;y sở hữu ngay cho m&igrave;nh thỏi son kem l&igrave; si&ecirc;u hot từ&nbsp;<strong>Maybelline New York!</strong></p>\r\n\r\n<h2>BẢNG M&Agrave;U</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>118 Dancer &ndash; Cam ch&aacute;y</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>135 Globetrotter &ndash; Cam gạch</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>117 Groundbreaker &ndash; Đỏ gạch</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>130 Self-starter &ndash; Hồng cam đ&agrave;o</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>220 Ambitious &ndash; Đỏ tươi</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>205 Assertive &ndash; Cam đ&agrave;o</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>245 Seeker &ndash; Đỏ n&acirc;u</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>150 Savant - Hồng lavender</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>170 Initiator - Hồng đất</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>175 Ringleader - Hồng đ&agrave;o</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>339 Innovator - Cam đỏ</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>335 Hustler - Đỏ cherry</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>355 Shaker - Gạch n&acirc;u socola</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>285 Gritty - Đỏ hồng đất</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>290 Standout - Đỏ cam</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>315 Extraordinary - Đỏ cam ng&atilde; đất</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>370 Overseer - Hồng đất</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>375 Peacekeeper - Đỏ đất</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>380 Enchanter - Hồng berry</strong></p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-kem-li-maybelline-16h-lau-troi-3.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Đặc điểm nổi bật:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Độ l&ecirc;n m&agrave;u chuẩn, cho m&agrave;u sắc rực rỡ tr&ecirc;n m&ocirc;i ngay từ lần đầu ti&ecirc;n.</p>\r\n	</li>\r\n	<li>\r\n	<p>Chất son cho hiệu ứng l&igrave; c&ugrave;ng khả năng giữ m&agrave;u l&acirc;u tr&ocirc;i l&ecirc;n đến 16 tiếng.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&ocirc;ng v&oacute;n cục, kh&ocirc;ng g&acirc;y bợn, th&ocirc; r&aacute;p tr&ecirc;n m&ocirc;i sau khi kh&ocirc;.</p>\r\n	</li>\r\n	<li>\r\n	<p>Thiết kế nhỏ xinh với m&agrave;u son giống hệt m&agrave;u vỏ b&ecirc;n ngo&agrave;i.</p>\r\n	</li>\r\n	<li>\r\n	<p>Độ che phủ ho&agrave;n hảo, cho khả năng phủ đều đến từng kẽ m&ocirc;i, kh&ocirc;ng l&agrave;m lộ v&acirc;n m&ocirc;i/r&atilde;nh m&ocirc;i.</p>\r\n	</li>\r\n	<li>\r\n	<p>Bảng m&agrave;u si&ecirc;u hot với những t&ocirc;ng m&agrave;u thời thượng v&agrave; cực chất.</p>\r\n	</li>\r\n	<li>\r\n	<p>Ph&ugrave; hợp với mọi đ&ocirc;i m&ocirc;i v&agrave; chinh phục cả những c&ocirc; n&agrave;ng kh&oacute; t&iacute;nh.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-kem-li-maybelline-16h-lau-troi-5.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-kem-chuan-li-giu-mau-16h-maybelline-new-york-city-edition-7.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://media.hasaki.vn/wysiwyg/HaNguyen/son-kem-chuan-li-giu-mau-16h-maybelline-new-york-city-edition-8.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Bảo quản:</h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t.</p>\r\n	</li>\r\n	<li>\r\n	<p>Tr&aacute;nh &aacute;nh nắng trực tiếp, nơi c&oacute; nhiệt độ cao hoặc ẩm ướt.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đậy nắp k&iacute;n sau khi sử dụng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2><strong>Th&ocirc;ng số sản phẩm:</strong></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>Dung t&iacute;ch:</strong>&nbsp;5ml</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Thương hiệu:</strong>&nbsp;Maybelline New York</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Mỹ</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Nơi sản xuất:</strong>&nbsp;Mỹ</p>\r\n	</li>\r\n</ul>\r\n', 1, 2, 10),
(48, 'Tẩy Tế Bào Chết Môi Cocoon ', 'son8', '70000', 65, '1655725980_son8.png', '<p><strong>Tẩy Tế B&agrave;o Chết M&ocirc;i Cocoon Từ C&agrave; Ph&ecirc; Đắk-Lắk 5g</strong>&nbsp;l&agrave;&nbsp;<a href=\"https://hasaki.vn/danh-muc/tay-te-bao-chet-moi-c190.html\" target=\"_blank\"><strong>tẩy tế b&agrave;o chết m&ocirc;i</strong></a>&nbsp;đến từ&nbsp;<a href=\"https://hasaki.vn/thuong-hieu/cocoon.html\" target=\"_blank\"><strong>thương hiệu mỹ phẩm thuần chay Cocoon</strong></a>&nbsp;của Việt Nam, chiết xuất từ c&aacute;c hạt<strong>&nbsp;c&agrave; ph&ecirc;</strong>&nbsp;Đắk Lắk rang xay bằng tay si&ecirc;u mịn v&agrave; c&aacute;c th&agrave;nh phần từ&nbsp;<strong>bơ hạt mỡ</strong>&nbsp;v&agrave; dầu&nbsp;<strong>mắc ca</strong>&nbsp;gi&uacute;p lấy đi c&aacute;c lớp bong tr&oacute;c, tế b&agrave;o chết tr&ecirc;n m&ocirc;i, cung cấp dưỡng ẩm đem lại đ&ocirc;i m&ocirc;i mềm mịn, hồng h&agrave;o, ẩm mượt.</p>\r\n', '<p>Hiện sản phẩm&nbsp;<strong>Tẩy Tế B&agrave;o Chết M&ocirc;i Cocoon Dak Lak Coffee Lip Scrub 5g&nbsp;</strong>đ&atilde; c&oacute; mặt</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Tẩy Tế Bào Chết Môi Cocoon Từ Cà Phê Đắk Lắk Dak Lak Coffee Lip Scrub 5g\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/tay-te-bao-chet-moi-cocoon-tu-ca-phe-dak-lak-5g-3.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Cocoon Đắk Lắk Dak-Lak Coffee Lip Scrub 5g \" src=\"https://media.hasaki.vn/wysiwyg/minhchau/tay-te-bao-chet-moi-cocoon-tu-ca-phe-dak-lak-5g-8.jpg\" style=\"height:800px; width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Loại da ph&ugrave; hợp:</strong></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Sản phẩm ph&ugrave; hợp với mọi loại da.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2><strong>Giải ph&aacute;p cho t&igrave;nh trạng:</strong></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Thường xuy&ecirc;n t&ocirc; son l&igrave;, son kem l&igrave;.</p>\r\n	</li>\r\n	<li>\r\n	<p>C&oacute; vẩy da chết m&ocirc;i, bong tr&oacute;c.</p>\r\n	</li>\r\n	<li>\r\n	<p>M&ocirc;i xỉn m&agrave;u &amp; th&acirc;m sạm.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2><strong>Ưu thế nổi bật:</strong></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Th&agrave;nh phần&nbsp;<strong>c&agrave; ph&ecirc;</strong>&nbsp;Đắk Lắk c&oacute; khả năng chống oxy h&oacute;a cao, lấy đi c&aacute;c tế b&agrave;o chết nhẹ nh&agrave;ng đem đến đ&ocirc;i m&ocirc;i hồng h&agrave;o, mềm mịn.</p>\r\n	</li>\r\n	<li>\r\n	<p>Chiết xuất&nbsp;<strong>bơ hạt mỡ</strong>&nbsp;gi&agrave;u vitamin A, E, F tăng cường dưỡng ẩm khiến đ&ocirc;i m&ocirc;i ẩm mượt, th&uacute;c đẩy sản sinh tế b&agrave;o, ngăn ngừa dấu hiệu l&atilde;o h&oacute;a.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Dầu mắc ca</strong>&nbsp;chứa 60% omega 9, squalene, vitamin E gi&uacute;p dưỡng ẩm cao, l&agrave;m mờ nếp r&atilde;nh m&ocirc;i, v&agrave; l&agrave;m dịu da nhanh ch&oacute;ng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<h2><strong>Độ an to&agrave;n:</strong></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Không chứa cồn</p>\r\n	</li>\r\n	<li>\r\n	<p>Không sulfate</p>\r\n	</li>\r\n	<li>\r\n	<p>Không dầu khoáng</p>\r\n	</li>\r\n	<li>\r\n	<p>Không paraben</p>\r\n	</li>\r\n	<li>\r\n	<p>Không vi hạt nhựa</p>\r\n	</li>\r\n	<li>\r\n	<p>Không thử nghiệm trên động vật</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Tẩy Tế Bào Chết Môi Cocoon Từ Cà Phê Đắk Lắk Dak Lak Coffee Lip Scrub\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/tay-te-bao-chet-moi-cocoon-tu-ca-phe-dak-lak-5g-1.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Tẩy Tế Bào Chết Môi Cocoon Từ Cà Phê Đắk Lắk Dak Lak Coffee Lip Scrub 5g Hasaki\" src=\"https://media.hasaki.vn/wysiwyg/minhchau/tay-te-bao-chet-moi-cocoon-tu-ca-phe-dak-lak-5g-2.jpg\" style=\"width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Bảo quản:</strong></h2>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Nơi kh&ocirc; r&aacute;o tho&aacute;ng m&aacute;t.</p>\r\n	</li>\r\n	<li>\r\n	<p>Tr&aacute;nh &aacute;nh nắng trực tiếp, nơi c&oacute; nhiệt độ cao hoặc ẩm ướt.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đậy nắp k&iacute;n sau khi sử dụng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Dung t&iacute;ch:</strong>&nbsp;5g</p>\r\n\r\n<p><strong>Thương hiệu:</strong>&nbsp;Cocoon</p>\r\n\r\n<p><strong>Xuất xứ thương hiệu:</strong>&nbsp;Việt Nam</p>\r\n\r\n<p><strong>Sản xuất tại:</strong>&nbsp;Việt Nam.</p>\r\n', 1, 2, 11);

